class Solution {
public:
    int isdivisible7(string num)
{
    int n = num.length(), gSum=0;
    if (n == 0)
        return 1;

    // Append required 0s at the beginning.
    if (n % 3 == 1) {
        num="00" + num;
        n += 2;
    }
    else if (n % 3 == 2) {
        num= "0" + num;
        n++;
    }

    // add digits in group of three in gSum
    int i, GSum = 0, p = 1;
    for (i = n - 1; i >= 0; i--) {

        // group saves 3-digit group
        int group = 0;
        group += num[i--] - '0';
        group += (num[i--] - '0') * 10;
        group += (num[i] - '0') * 100;

        gSum = gSum + group * p;

        // generate alternate series of plus
        // and minus
        p *= (-1);
    }

    return (gSum % 7 == 0);
}

    string largestPalindrome(int n, int k) {
        if(k==1 || k==3 || k==9){
            string ans = "";
            for(int i=0;i<n;i++){
                ans += "9";
            }
            return ans;
        }
        else if(k==2){
            if(n==1) return "8";
            else{
                string ans = "8";
                for(int i=1;i<n-1;i++){
                    ans += "9";
                }
                ans += "8";
                return ans;
            }
        }
        else if(k==8){
            if(n<=6){
                string ans ="";
                for(int i=0;i<n;i++){
                    ans+="8";
                }
                return ans;
            }
            else{
                string ans = "888";
                for(int i=3;i<n-3;i++){
                    ans += "9";
                }
                ans += "888";
                return ans;
            }
        }
        else if(k==5){
            if(n==1) return "5";
            else{
                string ans = "5";
                for(int i=1;i<n-1;i++){
                    ans += "9";
                }
                ans += "5";
                return ans;
            }
        }
        else if(k==6){
            if(n==1) return "6";
            else if(n==2) return "66";
            else{
                if(n%2){
                    string ans = "8";
                    for(int i=1;i<n/2;i++){
                        ans += "9";
                    }
                    string ff = ans + "8";
                    reverse(ans.begin(),ans.end());
                    ff += ans;
                    return ff;
                }
                else{
                    string ans = "8";
                    for(int i=2;i<n/2;i++){
                        ans += "9";
                    }
                    string ff = ans + "77";
                    reverse(ans.begin(),ans.end());
                    ff += ans;
                    return ff;
                }
            }
        }
        else if(k==4){
            if(n==1) return "8";
            else if(n==2) return "88";
            else if(n==3) return "888";
            else if(n==4) return "8888";
            else{
                string ans = "88";
                for(int i=2;i<n-2;i++){
                    ans += "9";
                }
                ans += "88";
                return ans;
            }
        }
        else if(k==7){
            if(n==1) return "7";
            else if(n==2) return "77";
            else{
                if(n%2){
                    string ans = "";
                    for(int i=0;i<n/2;i++){
                        ans += "9";
                    }
                    int f=9;
                    while(f>=0){
                        if(isdivisible7(ans + char(f+48) + ans)) break;
                        f--;
                    }
                    if(f<0) f++;
                    return ans + char(f+48) + ans;
                }
                else{
                    string ans = "";
                    for(int i=1;i<n/2;i++){
                        ans += "9";
                    }
                    int f = 9;
                    while(f>=0){
                        if(isdivisible7(ans + char(f+48) + char(f+48) + ans)) break;
                        f--;
                    }
                    if(f<0) f++;
                    return ans + char(f+48) + char(f+48) + ans; 
                }
            }
        }
        return "";
    }
};
