char* largestPalindrome(int n, int k) 
{
    //1全部，2末尾8，3或9全是9，8末尾888，5末尾5，4末尾88，6也有办法


    char *res = (char*)malloc(n+1);
    int i,j;


    if(k == 1 || k == 3 || k == 9)
    {
        for(i=0;i<n;i++)
            res[i] = '9';
    }

    if(k == 2)
    {
        res[0] = res[n-1] = '8';
        for(i=1;i<n-1;i++)
            res[i] = '9';
    }

    if(k == 5)
    {
        res[0] = res[n-1] ='5';
        for(i=1;i<n-1;i++)
            res[i] = '9';
    }

    if(k == 4)
    {
        if(n == 1)
            res[0] = '8';
        if(n == 2);
        {
            res[0] = '8';
            res[1] = '8';
        }

        if(n == 3)
        {
            res[0] = res[1] = res[2] = '8';
        }


        if(n >= 4)
        {
            res[0] = res[1] = res[n-2] = res[n-1] = '8';
            for(i=2;i<n-2;i++)
                res[i] = '9';
        }
    }


    if(k == 8)
    {
        for(i=0;i<n;i++)
            res[i] = '9';

        if(n == 1)
            res[0] = '8';
        if(n == 2)
            res[0] = res[1] = '8';
        if(n == 3)
            res[0] = res[1] = res[2] = '8';
        if(n >= 4)
            res[0] = res[1] = res[2] = res[n-1] = res[n-2] = res[n-3] = '8';
    }

    if(k == 6)
    {
        if(n == 1)  res[0] = '6';
        else if(n == 2)
            res[0] = res[1] = '6';

        else if(n  % 2)
        {
            for(i=0;i<n;i++)
                res[i] = '9';

            res[0] = res[n-1] = '8';
            res[(n-1)/2] = '8';
        }

        else
        {
            for(i=0;i<n;i++)
                res[i] = '9';

            res[0] = res[n-1] = '8';
            res[n / 2 - 1] = res[n / 2] = '7'; 
        }

    }


    if(k == 7)
    {
        int yushu[6] = {1,3,2,6,4,5};
        int sum = 0;

        if(n % 2)
        {
            for(i=0;i<(n-1)/2;i++)
            {
                sum = sum + 9 * yushu[i % 6] + 9 * yushu[(n-1-i) % 6]; 
                res[i] = res[n-1-i] = '9';
            }
            sum = sum % 7;

            for(i=9;i>=0;i--)
            {
                if( (sum + i * yushu[((n-1)/2) % 6] ) % 7 == 0)
                    break;
            }

            res[(n-1)/2] = i + '0';
        }


        else
        {
            for(i=0;i<n/2 - 1;i++)
            {
                sum = sum + 9 * yushu[i % 6] + 9 * yushu[(n-1-i) % 6]; 
                res[i] = res[n-1-i] = '9';
            }
            sum = sum % 7;

            for(i=9;i>=0;i--)
            {
                if( (sum + i * yushu[(n/2 - 1) % 6]  + i * yushu[(n/2) % 6]) % 7 == 0)
                    break;
            }

            res[n / 2 - 1]  = res[n / 2] = i + '0';
        }
    }

    res[n] = '\0';
    //printf("%c",res[0]);
    return res;
}
