class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:

        def f6(x):
            if x==3:
                return "888"
            else:
                if x%2==0:
                    return f"8{'9'*((x-4)//2)}77{'9'*((x-4)//2)}8"
                else:
                    return f"8{'9'*((x-3)//2)}8{'9'*((x-3)//2)}8"

        def f7(x):
            if x==3:
                return "959"
            elif x%6==0:
                return "9"*n
            choice = ["5", "77", "7", "", "4", "44", "6", "44", "4", "", "7", "77"][(x-3)%12]
            pad = ((n-len(choice))//2)
            return f"{'9'*(pad)}{choice}{'9'*(pad)}"


        if k in {1,3,9}:
            return "9"*n
        elif k==2:
            return "8" if n==1 else f"8{'9'*(n-2)}8"
        elif k==4:
            return "8"*n if n<=4 else f"88{'9'*(n-4)}88"
        elif k==5:
            return "5" if n==1 else f"5{'9'*(n-2)}5"
        elif k==8:
            return "8"*n if n<=6 else f"888{'9'*(n-6)}888"
        elif k==6:
            return "6"*n if n<=2 else f6(n)
        elif k==7:
            return "7"*n if n<=2 else f7(n)
