class Solution {
public:
    int f(int x)
    {
        x%=6;
        int p=1;
        for (int i=0;i<x;i++){
            p=(p*10)%7;
        }
        return p;
    }
    string largestPalindrome(int n, int k) {
        string ans="";
        if (k==1 || k==3 || k==9){
            for (int i=1;i<=n;i++) ans+='9';
        }
        else if (k==2){
            ans+='8';
            for (int i=1;i<=n-2;i++) ans+='9';
            if (n>1) ans+='8';
        }
        else if (k==4){
            if (n==1) ans+='8';
            else if (n==2) ans+="88";
            else if (n==3) ans+="888";
            else{
                ans+="88";
                for (int i=3;i<=n-2;i++) ans+='9';
                ans+="88";
            }
        }
        else if (k==5){
            if (n==1) ans+='5';
            else{
                ans+='5';
                for (int i=2;i<=n-1;i++) ans+='9';
                ans+='5';
            }
        }
        else if (k==6){
            if (n==1) ans+='6';
            else if (n==2) ans+="66";
            else{
                if (n%2){
                    ans+='8';
                    for (int i=1;i<=n/2-1;i++) ans+='9';
                    ans+='8';
                    for (int i=1;i<=n/2-1;i++) ans+='9';
                    ans+='8';
                }
                else{
                    ans+='8';
                    for (int i=1;i<=n/2-2;i++) ans+='9';
                    ans+="77";
                    for (int i=1;i<=n/2-2;i++) ans+='9';
                    ans+='8';
                }
            }
        }
        else if (k==7){
            if (n==1) ans+='7';
            else if (n==2) ans+="77";
            else{
                if (n%2==1){
                    int r=0;
                    for (int i=1;i<=n/2;i++){
                        ans+='9';
                        r+=9*f(n-i);
                        r%=7;
                    }
                   // cout<<r<<"\n";
                    for (int i=n/2+2;i<=n;i++){
                        r+=9*f(n-i);
                        r%=7;
                    }
                    //cout<<r<<"\n";
                    r=(7-r)%7;
                   // cout<<r<<"->\n";
                    for (int i=9;i>=0;i--){
                        char ch='0'+i;
                        int x=i*f(n-(n/2+1));
                        x%=7;
                        if (x==r){
                            ans+=ch;
                            break;
                        }
                    }
                    for (int i=1;i<=n/2;i++) ans+='9';
                }
                else{
                    int r=0;
                    for (int i=1;i<=n/2-1;i++){
                        ans+='9';
                        r+=9*f(n-i);
                        r%=7;
                    }
                   // cout<<r<<"\n";
                    for (int i=n/2+2;i<=n;i++) {
                        r+=9*f(n-i);
                        r%=7;
                    }
                   // cout<<r<<"\n";
                    r=(7-r)%7;
                   // cout<<r<<"->\n";
                    for (int i=99;i>=0;i-=11){
                        int k=i*f(n-(n/2)-1);
                        k%=7;
                        if (r==k){
                            char ch='0'+(i%10);
                            ans+=ch;
                            i/=10;
                            ch='0'+i;
                            ans+=ch;
                            break;
                        }
                    }
                    for (int i=n/2+2;i<=n;i++) ans+='9';
                }
            }
        }
        else{
            if (n<=6){
                for (int i=1;i<=n;i++) ans+='8';
            }
            else{
                ans+="888";
                for (int i=4;i<=n-3;i++) ans+='9';
                ans+="888";
            }
        }
        return ans;
    }
};
