import sys

class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        # if k == 1 or k == 3 or k == 9:
        #     d = "9"
        # elif k == 2 or k == 4 or k == 8:
        #     d = "8"
        # else:
        #     d = str(k)

        # if n == 1:
        #     return d
        # elif n == 2:
        #     return d + d
        # return d + self.largestPalindrome(n - 2, 1 if k == 2 or k == 5 else k) + d
        sys.set_int_max_str_digits(0)

        if k == 1 or k == 3 or k == 9:
            return "9" * n
        elif k == 2:
            if n == 1:
                return "8"
            return "8" + "9" * (n - 2) + "8"
        elif k == 4:
            if n <= 4:
                return "8" * n
            return "8" * 2 + "9" * (n - 4) + "8" * 2
        elif k == 8:
            if n <= 5:
                return "8" * n
            return "8" * 3 + "9" * (n - 6) + "8" * 3
        elif k == 5:
            if n == 1:
                return "5"
            return "5" + "9" * (n - 2) + "5"
        elif k == 6:
            if n == 1:
                return "6"
            elif n == 2:
                return "66"
            elif n == 3:
                return "8" * 3
            else:
                if n % 2 == 1:
                    return ("8" + "9" * (n // 2 - 1)) * 2 + "8"
                else:
                    s = "8" + "9" * (n // 2 - 2) + "7"
                    return s + s[::-1]
        elif k == 7:
            if n == 1:
                return "7"
            elif n == 2:
                return "77"
            for j in range(9, 0, -1):
                if n % 2 == 0:
                    s = "9" * (n // 2 - 1) + str(j) * 2 + "9" * (n // 2 - 1)
                else:
                    s = "9" * (n // 2) + str(j) + "9" * (n // 2)
                if int(s) % 7 == 0:
                    return s
