class Solution {
public:
    int isdivisible7(string num) {
        int n = num.length(), gSum=0;
        if (n == 0)
            return 1;

        // Append required 0s at the beginning.
        if (n % 3 == 1) {
            num="00" + num;
            n += 2;
        }
        else if (n % 3 == 2) {
            num= "0" + num;
            n++;
        }

        // add digits in group of three in gSum
        int i, GSum = 0, p = 1;
        for (i = n - 1; i >= 0; i--) {

            // group saves 3-digit group
            int group = 0;
            group += num[i--] - '0';
            group += (num[i--] - '0') * 10;
            group += (num[i] - '0') * 100;

            gSum = gSum + group * p;

            // generate alternate series of plus
            // and minus
            p *= (-1);
        }

        return (gSum % 7 == 0);
    }

    string givestring(char ch, int n) {
        string s = "";
        for(int i=0; i<n; i++) s.push_back(ch);
        return s;
    }
    string largestPalindrome(int n, int k) {
        string ans = "";
        if(k==1 || k==3 || k==9) return givestring('9',n);
        if(k==2) {
            ans = givestring('9',n);
            ans[0] = '8';
            ans[n-1] = '8';
            return ans;
        }
        if(k==4) {
            ans = givestring('9',n);
            ans[0] = '8';
            ans[n-1] = '8';
            if(n>=2) {
                ans[1] = '8';
                ans[n-2] = '8';
            }
            return ans;
        }
        if(k==8) {
            ans = givestring('9',n);
            ans[0] = '8';
            ans[n-1] = '8';
            if(n>=2) {
                ans[1] = '8';
                ans[n-2] = '8';
            }
            if(n>=3) {
                ans[2] = '8';
                ans[n-3] = '8';
            }
            return ans;
        }
        if(k==5) {
            ans = givestring('9',n);
            ans[0] = '5';
            ans[n-1] = '5';
            return ans;
        }
        if(k==6) {
            ans = givestring('9',n);
            if(n==1) return "6";
            if(n==2) return "66";

            ans[0] = '8';
            ans[n-1] = '8';
            if(n%2==1) {
                ans[n/2] = '8';
                return ans;
            } else {
                ans[n/2] = '7';
                ans[n/2-1] = '7';
                return ans;
            }
        }
        if(k==7) {
            string ans = givestring('9', n);
            if(n%2==1) {
                for(char ch='9'; ch>='1'; ch--) {
                    string temp = ans;
                    temp[n/2] = ch;
                    if(isdivisible7(temp)) return temp;
                }
            } else {
                for(char ch='9'; ch>='1'; ch--) {
                    string temp = ans;
                    temp[n/2] = ch;
                    temp[n/2-1] = ch;
                    if(isdivisible7(temp)) return temp;
                }
            }
        }
        return ans;
    }
};
