class Solution {
public:
    string largestPalindrome(int n, int k) {
        int m = (n + 1) >> 1, mod = 1, base, remain;
        string ans(n, '9');
        switch (k) {
        case 8:
            if (n > 4) ans[2] = ans[n-3] = '8';
        case 4:
            if (n > 2) ans[1] = ans[n-2] = '8';
        case 2:
            ans[0] = ans[n-1] = '8';
            break;
        case 5:
            ans[0] = ans[n-1] = '5';
            break;
        case 6:
            if (n < 3) {
                ans = string(n, '6');
                break;
            }
            ans[0] = ans[n-1] = '8';
            if (n & 1) ans[n >> 1] = '8';
            else ans[n>>1] = ans[(n>>1) - 1] =  '7';
            break;
        case 7:
            for (int i = 1; i < m; ++i) mod = mod * 10 % 7;
            if (n & 1) {
                base = 1;
                remain = (10 * mod + 1) * (mod + 6) % 7;
            } else {
                base = 11;
                remain = (100 * mod + 1) * (mod + 6) % 7;
            }
            for (int i = 9; i >= 0; --i) {
                if ((mod * base * i + remain) % 7 == 0) {
                    if (n & 1) ans[m-1] = '0' + i;
                    else ans[m] = ans[m-1] = '0' + i;
                    break;
                }
            }
            break;
        default:
            break;
        }
        return ans;
    }
};
