class Solution {
public:
    string generatePalindrome(const string& half, bool isOddLength) {
    string palindromeStr = half;
    if (isOddLength) {
        palindromeStr += string(half.rbegin() + 1, half.rend());
    } else {
        palindromeStr += string(half.rbegin(), half.rend());
    }
    return palindromeStr;
}

int modString(const string& numStr, int k) {
    int mod = 0;
    for (char digit : numStr) {
        mod = (mod * 10 + (digit - '0')) % k;
    }
    return mod;
}

void decrementString(string &numStr) {
    int n = numStr.size();
    int i = n - 1;
    while (i >= 0) {
        if (numStr[i] > '0') {
            numStr[i]--;
            break;
        } else {
            numStr[i] = '9';
            i--;
        }
    }
}

string seven(int n) {
    int halfLength = (n + 1) / 2;
    string half(halfLength, '9'); 
    string palindrome = generatePalindrome(half, n % 2 != 0);
    while (modString(palindrome, 7) != 0) {
        decrementString(half);
        palindrome = generatePalindrome(half, n % 2 != 0);
    }

    return palindrome;
}
    string largestPalindrome(int n, int k) {
        string ans = "";
        if(k==1 || k==3 || k==9){
            while(n--){
                ans+='9';
            }
            return ans;
        }
        else if(k==5){
            if(n==1){
                return "5";
            }
            ans+='5';
            n-=2;
            while(n--){
                ans+='9';
            }
            ans+='5';
            return ans;
        }
        else if(k==2 || k==4 || k==8){
            if(n==1){
                return "8";
            }
            if(n==2){
                return "88";
            }
            if(n==3 && k==2){
                return "898";
            }
            if(n==3 && k!=2){
                return "888";
            }
            if(n<=6 && k==8){
                while(n--){
                    ans+='8';
                }
                return ans;
            }
            int p = k;
            while(p%2==0){
                ans+='8';
                p/=2;
                n-=2;
            }
            while(n--){
                ans+='9';
            }
            while(k%2==0){
                ans+='8';
                k/=2;
            }
        }
        else if(k==6){
            if(n==1){
                return "6";
            }
            else if(n==2){
                return "66";
            }
            else if(n==3){
                return "888";
            }
            if(n==4){
                return "8778";
            }
            if(n==6){
                return "897798";
            }
            int s = n;
            ans+='8';
            n-=2;
            while(n--){
                ans+='9';
            }
            ans+='8';
            if(s%2==1){
                ans[s/2] = '8';
            }
            else{
                for(int i=s/2 - 1; i<s/2 + 1; i++){
                    ans[i]='7';
                }
            }
        }
        if(k==7){
            ans = seven(n);
            return ans;
        }
        return ans;
    }
};
