    public boolean f(String num) {
        int remainder = 0;

        for (int i = num.length() - 1; i >= 0; i--) {
            int digit = Character.getNumericValue(num.charAt(i));
            remainder = (remainder * 10 + digit) % 7;
        }

        return remainder == 0;
    }
