class Solution:
    def is_div(self, lnum, div):
        remainder = 0  # 初始化餘數為 0
        for digit_char in lnum:
            digit = int(digit_char)  # 將字元轉換為數字
            remainder = (remainder * 10 + digit) % div  # 更新餘數

        return remainder == 0

