class Solution {
private: 
    void makeDivisibleBy3(string &ans) {
        int n = ans.size();

        int sum = 0;
        for(auto x:ans) {
            sum += x-'0';
        }
        if(n&1) {
            int idx = n/2;
            while(sum%3!=0) {
                sum--;
                ans[idx]--;
            }

        } else {
            int idx = n/2; idx--;
            while(sum%3!=0) {
                sum-=2;
                ans[idx]--;
                ans[idx+1]--;
            }
        }
    }

    int remain(string s, int n) {
        int rem = 0, que = 0;
        for(int i=0;i<n;i++) {
            rem = (rem*10 + (s[i]-'0'))%7;
            // que = (que*10 + (s[i]-'0'))/7;
            // cout<<rem << " "<< que<<"\n";
        }
        // cout<<s<<" "<<rem<<"\n";
        return rem;

    }

    void makeDivisibleBy7(string &ans) {
        int n = ans.size();

        string temp = ans;
        temp[n-2] -= 4;
        int rem = remain(temp, n-1);

        while(rem != 0) {
            if(n&1) {
                int idx = n/2;
                temp[idx]--;
                ans[idx]--;
            } else {
                int idx = n/2;idx--;
                temp[idx]--;
                temp[idx+1]--;
                ans[idx]--;
                ans[idx+1]--;
            }
            rem = remain(temp, n-1);
        }
        // ans=temp;
    }
public:
    string largestPalindrome(int n, int k) {
        string ans="";
        if(k==1) {
            for(int i=0;i<n;i++) {
                ans += "9";
            }
        }

        if(k==2) {
            ans += "8";
            for(int i=1;i<n-1;i++) {
                ans += "9";
            }
            if(n>1)
                ans += "8";
        }

        if(k==3) {
            for(int i=0;i<n;i++) {
                ans += "9";
            }
        }

        if(k==4) {
            if(n<=4) {
                for(int i=0;i<n;i++) {
                    ans += "8";
                }
            } else {
                ans += "88";
                for(int i=2;i<n-2;i++) {
                    ans += "9";
                }
                ans += "88";
            }
        }

        if(k==5) {
            ans += "5";
            for(int i=1;i<n-1;i++) {
                ans += "9";
            }
            if(n>1) {
                ans += "5";
            }
        }

        if(k==6) {
            ans += "8";
            for(int i=1;i<n-1;i++) {
                ans += "9";
            }
            if(n>1)
            ans += "8";

            makeDivisibleBy3(ans);
        }

        if(k==7) {
            if(n<=2) {
                for(int i=0;i<n;i++) {
                    ans += "7";
                }
            } else {
                ans += "9";
                for(int i=1;i<n-1;i++) {
                    ans += "9";
                }
                ans += "9";
                makeDivisibleBy7(ans);
            }
        }

        if(k==8) {
            if(n<=6) {
                for(int i=0;i<n;i++) {
                    ans += "8";
                }
            } else {
                ans += "888";
                for(int i=3;i<n-3;i++) {
                    ans += "9";
                }
                ans += "888";
            }
        }

        if(k==9) {
            for(int i=0;i<n;i++) {
                ans += "9";
            }
        }

        return ans;
    }
};
