class Solution {
public:
    string largestPalindrome(int n, int k) {
        vector<int> p(n);
        p[0] = 1 % k;
        for (int i = 1; i < n; i++) p[i] = p[i - 1] * 10 % k;
        int m = n / 2;
        vector dp(n, vector<int>(k));
        for (int i = 0; i < 10; i++) {
            if (m == n - 1 && i == 0) continue;
            if (n % 2 == 1) dp[m][(i * p[m]) % k] = 1;
            else dp[m][(i * (p[m] + p[n - m - 1])) % k] = 1;
        }
        for (int i = m + 1; i < n; i++) {
            for (int j = 0; j < k; j++) {
                for (int d = 0; d < 10; d++) {
                    if (i == n - 1 && d == 0) continue;
                    dp[i][(j + d * (p[i] + p[n - i - 1])) % k] |= dp[i - 1][j];
                }
            }
        }
        string ans(n, '0');
        for (int i = n - 1, j = 0; i >= m; i--) {
            for (int d = 9; d >= 0; d--) {
                int t;
                if (i != m || n % 2 == 0) t = (j - d * (p[i] + p[n - i - 1]) % k + k) % k;
                else t = (j - d * p[i] % k + k) % k;
                if ((i != m && dp[i - 1][t]) || (i == m && t == 0)) {
                    ans[i] = '0' + d;
                    ans[n - i - 1] = '0' + d;
                    j = t;
                    break;
                }
            }
        }
        return ans;
    }
};
