class Solution {
public:
    int mod;

    int add(int x,int y){int res=(x+y)%mod;return res<0?res+mod:res;}
    int mul(int x,int y){int res=(x*1LL*y)%mod;return res<0?res+mod:res;}

    int power(int x,int y){
        int res=1; //x^y
        while(y){
            if(y&1) res=mul(res,x);
            x=mul(x,x);
            y=y>>1;
        }
        return res;
    }

    static const int N=100010;

    int dp[N][10];
    char ans[N];
    int n;

    #define dpp dp[id][rem]
    int solve(int id, int rem) {
        if((n-id+1) > id) return dpp=(rem==0); 
        if(dpp!=-1) return dpp;
        int prem=power(10, id-1);
        int rrem=0;
        if((n-id+1) < id) rrem=power(10, n-id);
        for(int i=9;i>=0;i--) {
            int curRem=add(mul(prem,i),mul(rrem,i));
            dpp=solve(id-1, add(curRem, rem));
            if(dpp==1) {
                ans[id]=i+'0';
                ans[n-id+1]=i+'0';
                return dpp;
            }
        }
        return dpp=0;
    }

    string largestPalindrome(int nn, int k) {
        n=nn;
        mod=k;
        memset(dp, -1, sizeof dp);
        solve(n,0);
        string fa="";
        for(int i=n;i>0;i--) {
            fa+=ans[i];
        }
        return fa;
    }
};
