class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k == 5:
            if n == 1:
                return "5"
            else:
                return "5" + "9" * (n - 2) + "5"
        if k == 2:
            if n == 1:
                return "8"
            else:
                return "8" + "9" * (n - 2) + "8"
        if k == 1 or k == 3 or k == 9:
            return "9" * n
        if k == 7:
            if n == 1:
                return "7"
            elif n == 2:
                return "77"
            else:
                i = 9
                while i >= 0:
                    if n % 2 == 1:
                        t = "9" * (n // 2) + str(i) + "9" * (n // 2)
                    else:
                        t = "9" * ((n // 2) - 1) + str(i) * 2 + "9" * ((n // 2) - 1)
                    res = 0
                    for ch in t:
                        res = (res * 10 + ord(ch) - ord('0')) % k
                        # res = res % k
                    if res == 0:
                        return str(t)
                    i -= 1
        if k == 6:
            if n == 1:
                return "6"
            elif n == 2:
                return "66"
            elif n % 2 == 1:
                return "8" + "9" * ((n // 2) - 1) + "8" + "9" * ((n // 2) - 1) + "8"
            else:
                return "8" + "9" * ((n // 2) - 2) + "77" + "9" * ((n // 2) - 2) + "8"
        if k == 4:
            return "88" + "9" + "9" * (n - 5) + "88" if n >= 5 else "8" * n
        if k == 8:
            return "888" + "9" + "9" * (n - 7) + "888" if n >= 7 else "8" * n
