    string largestPalindrome(int n, int k) {
        if (k == 1 || k == 3 || k == 9) {
            string s;
            for (int i = 0; i < n; i++) s += '9';
            return s;
        }
        if (k == 2) {
            string s = "8";
            for (int i = 0; i < n - 2; i++) s += '9';
            if (n > 1) s += '8';
            return s;
        }
        if (k == 5) {
            string s = "5";
            for (int i = 0; i < n - 2; i++) s += '9';
            if (n > 1) s += '5';
            return s;
        }
        if (k == 4) {
            string s;
            if (n <= 4) s = find(n, k);
            else {
                string t = find(4, k);
                s = t.substr(0, 2);
                for (int i = 0; i < n - 4; i++) {
                    s += '9';
                }
                s += t.substr(2, 2);
                return s;
            }
            return s;
        }
        if (k == 8) {
            string s;
            if (n <= 6) s = find(n, k);
            else {
                string t = find(6, k);
                s = t.substr(0, 3);
                for (int i = 0; i < n - 6; i++) {
                    s += '9';
                }
                s += t.substr(2, 3);
            }
            return s;
        }

        if (k == 6) {
            if (n <= 6) return find(n, k);
            else {
                string s = "89";
                n -= 4;
                if (n & 1) {
                    for (int i = 0; i < n / 2; i++) s += '9';
                    s += '8';
                    for (int i = 0; i < n / 2; i++) s += '9';
                } else {
                    n -= 2;
                    for (int i = 0; i < n / 2; i++) s += '9';
                    s += "77";
                    for (int i = 0; i < n / 2; i++) s += '9';
                }
                s += "98";
                return s;
            }
        }
        if (k == 7) {
            if (n <= 6) return find(n, k);
            if (n % 6 == 0) {
                string s;
                for (int i = 0; i < n; i++) s += '9';
                return s;
            } else {
                if (n == 7) return "9994999";
                if (n == 8) return "99944999";
                if (n == 9) return "999969999";
                if (n == 10) return "9999449999";
                if (n == 11) return "99999499999";
                if (n == 13) return "9999997999999";
                if (n >= 12) {
                    vector<string> add = {"", "7", "77", "959", "9779", "99799", "", "9994999", "99944999", "999969999", "9999449999", "99999499999"};
                    int a = n / 6, b = n % 6 + (a & 1) * 6;
                    cout << a << ' ' << b << endl;
                    string s;
                    for (int i = 0; i < a / 2; i++) {
                        s += "999999";
                    }
                    s += add[b];
                    for (int i = 0; i < a / 2; i++) {
                        s += "999999";
                    }
                    return s;
                }
            }
        }
        return "";
    }

    string find(int n, int k) {
        long long m = 1;
        for (int i = 0; i < n; i++) m *= 10;
        long long i = m - 1;
        while (i) {
            if (palindrome(i) && i % k == 0) {
                return to_string(i);
            }
            i--;
        }
        return "";
    }

    bool palindrome(long long x) {
        string s = to_string(x);
        for (int i = 0; i < s.size() / 2; i++) {
            if (s[i] != s[s.size() - i - 1]) return false;
        }
        return true;
    }
};
