class Solution {
public:
    string largestPalindrome(int n, int k) {
        string s = "";
        if (k == 1 or k == 9 or k==3) {
            for (int i = 0; i < n; i++) {
                s += '9';
            }
            return s;
        }
        if (k == 2) {
            s += '8';
            for (int i = 1; i < n - 1; i++) {
                s += '9';
            }
            if (n != 1)
                s += '8';
            return s;
        }
        if (k == 4) {
            s += '8';
            if (n > 1)
                s += '8';
            for (int i = 2; i < n - 2; i++) {
                s += '9';
            }
            if (n > 2)
                s += '8';
            if (n > 3)
                s += '8';
            return s;
        }
        if (k == 5) {
            s += '5';
            for (int i = 1; i < n - 1; i++)
                s += '9';
            if (n > 1)
                s += '5';
            return s;
        }
        if (k == 6) {
            if (n == 1) {
                s += '6';
                return s;
            }
            if (n == 2) {
                s += '6';
                s += '6';
                return s;
            }
            int sum = 0;
            // string str;
            for(int i=0;i<n;i++){
                s+='9';
            }
            s[0]='8';
            s[n-1]='8';

            for(int i=0;i<n;i++){
                if(!(i==n/2 || i==(n-1)/2)){
                    sum+=s[i]-'0';
                }
            }

            if (n & 1) {
                for (char i = '9'; i >= '0'; i--) {
                    if ((sum + (i - '0')) % 3 == 0) {
                        s[n/2]= i;
                        break;
                    }
                }
            } else {
                for (int i = 9; i >= 0; i--) {
                    if ((sum + 2*(i)) % 3 == 0) {
                        char x = '0';
                        int c = x + (i);
                        s[n/2] =char(c);
                        s[(n-1)/2] = char(c);
                        break;
                    }
                }
            }

            return s;
        }
        if (k == 8) {
            if (n <= 6) {
                for (int i = 1; i <= n; i++)
                    s += '8';
                return s;
            }
            s += '8';
            s += '8';
            s += '8';
            for (int i = 1; i <= n - 6; i++)
                s += '9';
            s += '8';
            s += '8';
            s += '8';
            return s;
        }
        if(k==7){
            if(n==1){
                return "7";
            }
            else if(n==2){
                return "77";
            }
            for(int i=0;i<n;i++){
                s+='9';
            }
            string ans=s;
            for(char x='1';x<='9';x++){
                if(n%2==0){
                    s[(n-1)/2]=x;
                    s[n/2]=x;

                    vector<int> v={1,3,2,6,4,5};
                    int diff=0;
                    int j=0;
                    for(int i=n-1;i>=0;i--){
                        int val= (s[i]-'0')*v[j];
                        j++;

                        j%=6;
                        val%=7;
                        diff+=val;
                        diff%=7;
                    }
                    if(diff==0){
                        ans=s;
                    }
                }
                else{
                    // s[(n-1)/2]=x;
                    s[n/2]=x;

                    vector<int> v={1,3,2,6,4,5};
                    int diff=0;
                    int j=0;
                    for(int i=n-1;i>=0;i--){
                        int val= (s[i]-'0')*v[j];
                        j++;
                        j%=6;
                        val%=7;
                        diff+=val;
                        diff%=7;
                    }
                    if(diff==0){
                        ans=s;
                    }
                }
            } 
            return ans;     
        }
        return s;
    }
};
