class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k == 1 or k == 3 or k == 9:
            return "9" * n
        if k == 2:
            if n <= 2:
                return "8" * n
            else:
                return "8" + ("9" * (n - 2)) + "8"
        if k == 4:
            if n <= 4:
                return "8" * n
            else:
                return "88" + ("9" * (n - 4)) + "88"
        if k == 5:
            if n <= 2:
                return "5" * n
            else:
                return "5" + ("9" * (n - 2)) + "5"
        if k == 8:
            if n <= 6:
                return "8" * n
            else:
                return "888" + ("9" * (n - 6)) + "888"
        if k == 6:
            if n == 1:
                return "6"
            if n == 2:
                return "66"
            if n % 2 == 1:
                res = "8" + ("9" * (n - 2)) + "8"
                res = res[: n // 2] + "8" + res[n // 2 + 1 :]
                return res
            else:
                res = "8" + ("9" * (n - 2)) + "8"
                res = res[: n // 2 - 1] + "77" + res[n // 2 + 1 :]
                return res
        if k == 7:
            if n <= 2:
                return '7' * n

            if n % 2 == 1:
                for i in range(9, -1, -1):
                    v = (10 ** n - 1) - ((9 - i) * (10 ** (n // 2)))
                    sv = ('9' * (n // 2)) + str(i) + ('9' * (n // 2))
                    if v % 7 == 0:
                        return sv
            else:            
                for i in range(9, -1, -1):
                    v = (10 ** n - 1) - (11 * (9 - i) * (10 ** ((n - 1) // 2)))
                    sv = ('9' * (n // 2 - 1)) + str(i) + str(i) + ('9' * (n // 2 - 1))
                    if v % 7 == 0:
                        return sv
