class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if n <= 5:
            i = 10 ** n - 1
            while i > 0:
                if i % k == 0 and str(i) == str(i)[::-1]:
                    return str(i)
                i -= 1

        if k == 1 or k == 3 or k == 9:
            return '9' * n

        if k == 2:
            return '8' + '9' * (n - 2) + '8'

        if k == 4:
            return '88' + '9' * (n - 4) + '88'

        if k == 5:
            return '5' + '9' * (n - 2) + '5'

        if k == 8:
            return '888' + '9' * (n - 6) + '888'

        if k == 6:
            if n % 2 == 1:
                t = (n - 3) // 2
                return '8' + '9' * t + '8' + '9' * t + '8'
            else:
                t = (n - 4) // 2
                return '8' + '9' * t + '77' + '9' * t + '8'

        if k == 7:
            m = 1
            for _ in range(n):
                m = m * 10 % 7
            m = (m + 6) % 7

            if n % 2 == 0:
                a = 1
                for _ in range(n // 2 - 1):
                    a = a * 10 % 7
                a = (a + a * 10 % 7) % 7

                for i in reversed(range(10)):
                    if m == 0:
                        return '9' * (n // 2 - 1) + str(i) + str(i) + '9' * (n // 2 - 1)
                    m = (m - a) % 7
            else:
                a = 1
                for _ in range(n // 2):
                    a = a * 10 % 7

                for i in reversed(range(10)):
                    if m == 0:
                        return '9' * (n // 2) + str(i) + '9' * (n // 2)
                    m = (m - a) % 7

        return ''
