class Solution {
public:
    string largestPalindrome(int n, int k) {
        string s;
        if(k==1||k==3||k==9){
            s= string(n,'9');
            return s;
        }
        if(k==5){

            if(n==1){
                s="5";
            }
            else {
                s="5";
                s+=string(n-2,'9');
                s+="5";
            }
            return s;
        }
        if(k==2){
            if(n==1)s="8";
            else{ 
                s="8";
                s+=string(n-2,'9');
                s+="8";
            }
            return s;
        }
        if(k==4){
            if(n<=4){
                return string(n,'8');
            }
            return "88"+string(n-4,'9')+"88";
        }
        if(k==8){
            if(n<=6){
                return string(n,'8');
            }
            return "888"+string(n-6,'9')+"888";
        }
        if(k==6){
            if(n<=2)return string(n,'6');
            if(n==3)return "888";
            if(n==4)return "8778";
            if(n&1){
                int x=(n-3)/2;
                return "8"+string(x,'9')+"8"+string(x,'9')+"8";
            }
            int x=(n-4)/2;
            return "8"+string(x,'9')+"77"+string(x,'9')+"8";
        }
        if(n<=2)return string(n,'7');
        int x=(n-1)/2;

        s=string(n,'9');
        string t;
        int f=9;
        while(f>0){
            int rem=0;

            for(int i=0;i<n;i++){
                rem*=10;
                rem+=s[i]-'0';
                rem%=7;
            }
            if(rem==0){
                t=s;
                break;
            }

            f--;
            s[x]=char(f+'0');
            if(n%2==0)s[x+1]=s[x];
        }
        return t;
    }
};
