class Solution {
public:
int mod(string num, int a)
{
    int res=0;
    for (int i = 0; i < num.length(); i++)
        res = (res * 10 + num[i] - '0') % a;

    return res;
}
    string largestPalindrome(int n, int k) {
        if(k==1 || k==3 || k==9){
            string A="";
            for(int i=0;i<n;i++){
                A+='9';
            }
            return A;
        }else if(k==2){
            if(n==1){
                return "8";
            }
            string A="";
            for(int i=0;i<n-2;i++){
                A+='9';
            }
            A+='8';
            A='8'+A;
            return A;
        }else if(k==4){
            string A="";
            if(n<4){
                for(int i=0;i<n;i++){
                    A+='8';
                }
                return A;
            }
            for(int i=0;i<n-4;i++){
                A+='9';
            }
            A+="88";
            A="88"+A;
            return A;
        }else if(k==5){
            if(n==1){
                return "5";
            }
            string A="";
            for(int i=0;i<n-2;i++){
                A+='9';
            }
            A+='5';
            A='5'+A;
            return A;
        }else if(k==6){
            if(n==1){
                return "6";
            }
            if(n==2){
                return "66";
            }
            string A="";
            for(int i=0;i<n-2;i++){
                A+='9';
            }
            A+='8';
            A='8'+A;
            if(n%2==1){
                A[n/2]='8';
            }else{
                A[n/2]='7';
                A[(n/2)-1]='7';
            }
            return A;
        }else if(k==8){
            string A="";
            if(n<=6){
                for(int i=0;i<n;i++){
                    A+='8';
                }
                return A;
            }
            for(int i=0;i<n-6;i++){
                A+='9';
            }
            A+="888";
            A="888"+A;
            return A;
        }else{
            if(n==1){
                return "7";
            }
            if(n==2){
                return "77";
            }
            string A="";
            for(int i=0;i<n;i++){
                A+='9';
            }
            for(char i='9';i>='0';i--){
                if(n%2==1){
                    A[n/2]=i;
                    if(mod(A,7)==0){
                        return A;
                    }
                }else{
                    A[n/2]=i;
                    A[n/2-1]=i;
                    if(mod(A,7)==0){
                        return A;
                    }
                }
            }
            return A;
        }
    }
};
