class Solution {
public:
    string largestPalindrome(int n, int k) {
        string ans;
        if(k==1 || k==3 || k==9)
        {
            ans = string(n, '9');
            return ans;
        }
        if(k==2)
        {
            if(n==1) return "8";
            else if(n==2) return "88";
            else return "8" + string(n-2, '9') + "8";
        }
        if(k==4)
        {
            if(n<=4) return string(n, '8');
            else return "88" + string(n-4, '9') + "88";

        }
        if(k==5)
        {
            if(n==1) return "5";
            else if(n==2) return "55";
            else return "5" + string(n-2, '9') + "5";
        }
        if(k==8)
        {
            if(n<=6) return string(n, '8');
            else return "888" +string(n-6, '9') + "888";
        }
        if(k==6)
        {
            if(n==1) return "6";
            else if(n==2) return "66";
            else if(n==3) return "888";
            else if(n>=4 && n%2 == 0) return "8" + string((n-4)/2,'9') + "77" + string((n-4)/2,'9') + "8";
            else if(n>=4 && n%2 == 1) return "8" + string((n-3)/2,'9') + "8" + string((n-3)/2,'9') + "8";
        }
        if(k==7)
        {
            if(n==1) return "7";
            else if(n==2) return "77";
            else if(n==3) return "959";
            else{
                if(n%2 == 1)
                {
                    int rec = 1;
                    int jieguo = 0;
                    int yao;
                    for(int i = 1; i<=n ;i++)
                    {
                        if(i == n/2 + 1)
                        {
                            rec = (rec * 10) % 7;
                            yao = rec;
                        }
                        else
                        {
                            if(i > 1)
                                rec = (rec * 10) % 7;
                            jieguo = (jieguo + 2*rec) % 7;
                        }
                    }
                    int k;
                    for(k = 9; k>=0; k--)
                    {
                        if((k*yao + jieguo)% 7==0)
                            break;
                    }
                    return string((n-1)/2,'9') + to_string(k) + string((n-1)/2,'9');
                }




                if(n%2 == 0)
                {
                    int rec = 1;
                    int jieguo = 0;
                    int yao = 0;
                    for(int i = 1; i<=n ;i++)
                    {
                        if(i == n/2 + 1 || i == n/2)
                        {
                            rec = (rec * 10) % 7;
                            yao = (yao + rec) % 7;
                        }
                        else
                        {
                            if(i > 1)
                                rec = (rec * 10) % 7;
                            jieguo = (jieguo + 2*rec) % 7;
                        }
                    }
                    cout<< jieguo<<yao;
                    int k;
                    for(k = 9; k>=0; k--)
                    {
                        if((k*yao + jieguo)% 7==0)
                            break;
                    }
                    //cout<<k;
                    return string((n-2)/2,'9') + to_string(k)+ to_string(k) + string((n-2)/2,'9');
                }
            }
        }

        return ans;
    }
};
