class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if n == 1:
            for i in range(9,-1,-1):
                if i % k == 0:
                    return str(i)
        if n == 2:
            for i in range(9,-1,-1):
                if (i*11) % k == 0:
                    return str(i*11)
        if n == 3:
            for i in range(99,9,-1):
                p = i*10 + i//10
                if p % k == 0:
                    return str(p)
        if n <= 6:
            for i in range(10**n-1,-1,-1):
                if i%k:continue
                s = str(i)
                if s == s[::-1]:
                    return s 


        ans = [0]*n 
        if k == 4:
            for i in range(99,9,-1):
                x,y = divmod(i,10)
                if (y*10 + x)%4 == 0:
                    ans[0]= ans[n-1] = x 
                    ans[1] = ans[n-2] = y
                    break
            for i in range(2,n-2):
                ans[i] = 9 
            return ''.join(map(str,ans))
        elif k == 8:
            for i in range(999,99,-1):
                x = i//100
                y = i//10%10
                z = i%10 
                if (z*100+y*10+x)%k == 0:
                    ans[0] = ans[n-1] = x
                    ans[1] = ans[n-2] = y 
                    ans[2] = ans[n-3] = z 
                    for i in range(3,n-3):
                        ans[i] = 9
                    return ''.join(map(str,ans)) 
        pw10 = [1]*n 
        for i in range(1,n):
            pw10[i] = pw10[i-1]*10%k 
        s = 0 
        mx = 0
        for i in range(10):
            mx = max(mx, i*k%10)
        ans[0] = ans[-1] = mx 
        s = (s+mx+mx*pw10[n-1])%k
        l,r = 1,n-2
        while l + 2 < r:
            ans[l] = ans[r] = 9 
            s = (s+9*pw10[l]+9*pw10[r])%k 
            l += 1
            r -= 1

        if n & 1:
            for i in range(99,9,-1):
                x,y = divmod(i,10)
                ans[l]=ans[r] = x 
                ans[l+1] = y 
                p = (s+x*pw10[l]+y*pw10[l+1]+x*pw10[r])%k 
                if p%k == 0:
                    return ''.join(map(str,ans))
        else:
            # print(ans,s,l,r)
            for x in range(9,-1,-1):
                ans[l] = ans[r] = x 
                p = (s + x*pw10[l]+x*pw10[r])%k 
                if p%k == 0:
                    return ''.join(map(str,ans))
