class Solution {
public:
    bool calc(int n, int i, int k, string& s, int curr)
    {
        if((i>n/2&&n%2==1) ||(n%2==0&&i>=n/2))
        {
            //cout<<i<<"  "<<s<<endl;
            for(int j=n/2-1;j>=0;j--)
            {
               int dig = s[j]-'0'; 
                curr=(curr*10+dig)%k;
                s[i+n/2-1-j]=s[j];
            }
            return curr==0;       
        }
       else
       {
          for(int j=9;j>=(i>0?0:1);j--)
          {
            if(i==0)
            {
                if(k==5&&j%k!=0)
                   continue;
                 if(k%2==0&&j%2==1)
                   continue;  
            }
            if(i==1)
            {
                if(k==4&&(j*10+(s[0]-'0'))%4!=0)
                   continue;
            }
            if(i==2)
            {
                if(k==8&&(j*100+(s[1]-'0')*10+(s[0]-'0'))%8!=0)
                   continue;
            }

            char ch = (char)(j+'0');
            s[i]=ch;
            if(calc(n, i+1, k, s, (curr*10+j)%k))
               return true;
          }
          return false;
       } 
    }
    string largestPalindrome(int n, int k) {
        string s="";
        for(int i=0; i<n;i++)
        {
            s+='9';
        }
        calc(n, 0, k, s, 0);
        return s;
    }
};
