class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        seven=['5','77','7','99','4','44','6','44','4','99','7','77']
        ans=[None]*n

        if k==1 or k==3 or k==9:
            return '9'*n

        elif k==2:
            ans[-1]='8'
            ans[0]='8'
            for i in range(1,n-1):
                ans[i]='9'

        elif k==2:
            ans[-1]='8'
            ans[0]='8'
            for i in range(1,n-1):
                ans[i]='9'

        elif k==4:
            if n<5:
                return '8'*n
            else:
                return '8'*2+ '9'*(n-4) +'8'*2

        elif k==5:
            if n==1:
                return '5'
            return '5'+'9'*(n-2)+'5'

        elif k==6:
            if n==1:
                return '6'
            elif n==2:
                return '66'
            elif n%2:
                ans[0],ans[-1],ans[n//2]='8','8','8'
            else:
                ans[0],ans[-1],ans[n//2],ans[n//2-1]='8','8','7','7'
            for i in range(n):
                if ans[i]==None:
                    ans[i]='9'

        elif k==7:
            if n==1:
                return '7'
            elif n==2:
                return '77'
            else:
                mid=seven[(n-3)%12]
                t=len(mid)
                return '9'*((n-t)//2)+mid+'9'*((n-t)//2)

        elif k==8:
            if n<7:
                return '8'*n
            else:
                return '8'*3+ '9'*(n-6) +'8'*3

        return ''.join(ans)
