class Solution {
public:
    string largestPalindrome(int n, int k) 
    {
        string s = "";
        if(k==1 || k==3 || k==9)
        {
            for(int i=0; i<n; i++)
            s.push_back('9');
        }
        if(k == 2 || k == 4)
        {
            if(n <= k)
            {
                for(int i=0; i<n; i++)
                s.push_back('8');
            }
            else
            {
                for(int i=0; i<k/2; i++)
                s.push_back('8');
                for(int i=0; i<n-k; i++)
                s.push_back('9');
                for(int i=0; i<k/2; i++)
                s.push_back('8');
            }
        }
        if(k == 8)
        {
            if(n <= 6)
            {
                for(int i=0; i<n; i++)
                s.push_back('8');
            }
            else
            {
                for(int i=0; i<3; i++)
                s.push_back('8');
                for(int i=0; i<n-6; i++)
                s.push_back('9');
                for(int i=0; i<3; i++)
                s.push_back('8');
            }
        }
        if(k == 5)
        {
            if(n <= 2)
            {
                for(int i=0; i<n; i++)
                s.push_back('5');
            }
            else
            {
                s.push_back('5');
                for(int i=0; i<n-2; i++)
                s.push_back('9');
                s.push_back('5');
            }
        }
        if(k == 6)
        {
            if(n <= 2)
            {
                for(int i=0; i<n; i++)
                s.push_back('6');
            }
            else
            {
                if(n&1)
                {
                    s.push_back('8');
                    for(int t=0; t<2; t++)
                    {
                        for(int i=1; i<n/2; i++)
                        s.push_back('9');
                        s.push_back('8');
                    }
                }
                else
                {
                    s.push_back('8');
                    for(int i=2; i<n/2; i++)
                    s.push_back('9');
                    s.push_back('7');
                    s.push_back('7');
                    for(int i=2; i<n/2; i++)
                    s.push_back('9');
                    s.push_back('8');
                }
            }
        }
        if(k == 7)
        {
            if(n <= 2)
            {
                for(int i=0; i<n; i++)
                s.push_back('7');
            }
            else
            {
                vector<int> diff = {1,3,2,6,4,5};
                if(n%6 == 0)
                {
                    for(int i=0; i<n ;i++)
                    s.push_back('9');
                    return s;
                }
                if(n%6 == 1 || n%6 == 3 || n%6 == 5)
                {
                    int x = 9;
                    int d = diff[n%6] - 1;
                    int m = (n/2)%6;
                    for(int i=0; i<=9; i++)
                    {
                        if((d - i*diff[m])%7 == 0)
                        {
                            x = 9-i; 
                            break;
                        }
                    }
                    for(int i=0; i<n/2; i++)
                    s.push_back('9');
                    s.push_back('0'+x);
                    for(int i=0; i<n/2; i++)
                    s.push_back('9');
                }
                if(n%6 == 2 || n%6 == 4)
                {
                    int x = 9;
                    int d = diff[n%6] - 1;
                    int m1 = (n/2 - 1)%6, m2 = (m1+1)%6;
                    for(int i=0; i<=9; i++)
                    {
                        if((d - i*diff[m1] - i*diff[m2])%7 == 0)
                        {
                            x = 9-i; 
                            break;
                        }
                    }
                    for(int i=1; i<n/2; i++)
                    s.push_back('9');
                    s.push_back('0'+x);
                    s.push_back('0'+x);
                    for(int i=1; i<n/2; i++)
                    s.push_back('9');
                }
            }
        }
        return s;
    }
};
