class Solution {
public:
    string largestPalindrome(int n, int k) {
          string r="";
        for(int i=1;i<=n;i++)
        {
                r+="9";
        }
        if(k==1 || k==3 || k==9)
        {
            return r;
        }
        else if(k==2)
        {
            r.pop_back();
            r+="8";
            r[0]='8';
            return r;
        }
        else if(k==4)
        {
            if(r.size()==1) return "8";
            else if(r.size()==2) return "88";
            else if(r.size()==3) return "888";
            int size=r.size();
            r[size-2]='8';
            r[size-1]='8';
            r[0]='8';
            r[1]='8';
            return r;
        }
        else if(k==5)
        {
            r.pop_back();
            r+="5";
            r[0]='5';
            return r;
        }
        else if(k==6)
        {
            if(r.size()==1) return "6";
            else if(r.size()==2) return "66";
            else if(r.size()==3) return "888";
            else if(n%2==0)
            {
                string temp="";
                temp+="8";
                for(int i=2;i<n/2;i++) temp+="9";
                temp+="77";
                for(int i=n/2+2;i<n;i++) temp+="9";
                temp+="8";
                return temp;
            }
            else
            {
                string temp="";
                temp+="8";
                for(int i=2;i<=n/2;i++) temp+="9";
                temp+="8";
                for(int i=n/2+2;i<n;i++) temp+="9";
                temp+="8";
                return temp;
            }
        }
        else if(k==7)
        {
            vector<int> v(13);
            v[1]=7;
            v[2]=77;
            v[3]=5;
            v[4]=77;
            v[5]=7;
            v[6]=99;
            v[7]=4;
            v[8]=44;
            v[9]=6;
            v[10]=44;
            v[11]=4;
            v[12]=99;
            if(n==1) return "7";
            else if(n==2) return "77";
            int val=n%12;
            if(val==0)
            {
                string temp="";
                for(int i=0;i<n;i++) temp+="9";
                return temp;
            }
            else
            {
                string qq=to_string(v[val]);
                int len=n-qq.size();
                string temp="";
                for(int i=1;i<=len/2;i++) temp+="9";
                temp+=qq;
                int count=qq.size();
                for(int i=len/2+count+1;i<=n;i++) temp+="9";
                return temp;
            }
        }
        else
        {
            string temp="";
            for(int i=1;i<=n;i++)
            {
                temp+="8";
            }
            if(n<=6) return temp;
            for(int i=3;i<n-3;i++) temp[i]='9';
            return temp;
        }
        return "";
    }
};
