class Solution {
public:
    string largestPalindrome(int n, int k) {
        if (k == 1 || k == 3 || k == 9){
            string res = "";
            for (int i = 0; i < n; i++) res += '9';
            return res;
        }

        if (k == 2){
            string res = "8";
            for (int i = 0; i < n - 2; i++) res += '9';
            if (n > 1)  res += '8';
            return res;
        }

        if (k == 4){
            if (n == 1) return "8";
            if (n == 2) return "88";
            if (n == 3) return "888";

            string res = "88";
            for (int i = 0; i < n - 4; i++) res += '9';
            res += "88";
            return res;
        }

        if (k == 5){
            string res = "5";
            for (int i = 0; i < n - 2; i++) res += '9';
            if (n > 1)  res += '5';
            return res;
        }

        if (k == 6){
            if (n == 1) return "6";
            if (n == 2) return "66";

            string res = "";
            if (n&1){
                res += '8';
                for (int i = 0; i < (n - 3) >> 1; i++)  res += '9';
                res += '8';
                for (int i = 0; i < (n - 3) >> 1; i++)  res += '9';
                res += '8';
                return res;
            }

            res += '8';
            for (int i = 0; i < (n - 4) >> 1; i++)  res += '9';
            res += "77";
            for (int i = 0; i < (n - 4) >> 1; i++)  res += '9';
            res += '8';
            return res;
        }

        if (k == 7){
            if (n == 1) return "7";
            if (n == 2) return "77";
            string res = "";
            int half = (n - 1) >> 1;
            int mid = 2 - (n&1);
            int rem = 0;
            int cur = 1;
            for (int i = 0; i < half; i++){
                rem = (rem*10 + 2) % 7;
                res += '9';
            }
            for (int i = 0; i < mid; i++)
                rem = rem*10 % 7;
            for (int i = 0; i < half; i++){
                rem = (rem*10 + 2) % 7;
                cur = cur*10 % 7;
            }

            int t = 0;
            int f = 0;
            if (mid == 1){
                t = 9;
                f = 1;
            }
            else{
                t = 99;
                f = 11;
            }

            for (int i = t; i >= 0; i -= f)
                if ((i*cur + rem)%7==0){
                    string c_mid = "";
                    if (i < 10) c_mid = (char)(i + '0');
                        else{
                            c_mid = (char)(i/10 + '0');
                            c_mid += (char)(i%10 + '0');
                        }
                    res = res + c_mid + res;
                    break;
                }
            return res;
        }

        if (k == 8){
            string res = "";
            if (n < 6){
                for (int i = 0; i < n; i++) res += '8';
                return res;
            }

            res += "888";
            for (int i = 0; i < n - 6; i++) res += '9';
            res += "888";
            return res;
        }
        return "";
    }
};
