class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k == 1 or k == 3 or k == 9:
            return "9" * n
        if k == 0 or k == 5:
            if n == 1:
                return "5"
            else:
                return "5" + "9" * (n - 2) + "5"
        if k == 2:
            if n == 1:
                return "8"
            return "8" + "9" * (n - 2) + "8"
        if k == 4:
            if n == 1:
                return "8"
            if n == 2:
                return "88"
            if n == 3:
                return "888"
            return "88" + "9" * (n - 4) + "88"
        if n == 1:
            return str(k)
        if k == 8:
            if n <= 5:
                return "8" * n
            else:
                return "888" + "9" * (n - 6) + "888"
        if k != 7:
            if n % 2:
                li = []
                for i in range(100, 1000):
                    if str(i) == str(i)[::-1]:
                        li.append(i)
                if n == 3:
                    for x in li[::-1]:
                        if x % k == 0:
                            return str(x)
