class Solution {
    public String largestPalindrome(int n, int k) {
        char[] ch = new char[n];
        Arrays.fill(ch,'9');
        if(k==1 || k==3 || k==9) {

        } else if(k==2) {
            ch[0] = '8';
            ch[n-1] = '8';       
        } else if(k==4) {
            if(n<=2) {
                ch[0] = '8';
                ch[n-1] = '8';                  
            } else {
                ch[0] = '8';
                ch[1] = '8';
                ch[n-1] = '8';
                ch[n-2] = '8';
            }
        } else if(k==5) {
            ch[0] = '5';
            ch[n-1] = '5';
        } else if(k==6) {
            if(n<=2) {
                ch[0] = '6';
                ch[n-1] = '6';
            } else if(n%2==1) {
                ch[0] = '8';
                ch[n-1] = '8';
                ch[n/2] = '8';
            } else {
                ch[0] = '8';
                ch[n-1] = '8';
                ch[n/2] = '7';
                ch[n/2-1] = '7';
            }
        } else if(k==7) {
            while(!check(ch)) {
                if(n%2==0) {
                    ch[n/2] --;
                    ch[n/2-1]--;
                } else {
                    ch[n/2] --;
                }
            }
        } else if(k==8) {
            if(n<=6) {
                Arrays.fill(ch,'8');
            } else {
                ch[0] = '8';
                ch[1] = '8';
                ch[2] = '8';
                ch[n-1] = '8';
                ch[n-2] = '8';
                ch[n-3] = '8';
            }
        }
        return new String(ch);    
    }
    boolean check(char[] ch) {
        int res = 0;
        for(char c:ch) {
            res *= 10;
            res += c-'0';
            res %= 7;
        }
        return res==0;
    }
}
