class Solution(object):
    def largestPalindrome(self, n, k):
        """
        :type n: int
        :type k: int
        :rtype: str
        """
        if k in (1, 3, 9):
            return "9" * n
        if k == 2:
            if n == 1:
                return "8"
            else:
                return "8" + "9" * (n-2) + "8"
        if k == 4:
            if n <= 4:
                return "8" * n
            else:
                return "88" + "9" * (n-4) + "88"
        if k == 5:
            if n == 1:
                return "5"
            else:
                return "5" + "9" * (n-2) + "5"
        if k == 6:
            if n == 1:
                return "6"
            elif n == 2:
                return "66"
            elif n % 2 == 1:
                return "8" + "9"*(n//2-1) + "8" + "9"*(n//2-1) + "8"
            elif n % 2 == 0:
                return "8" + "9"*(n//2-2) + "77" + "9"*(n//2-2) + "8"
        if k == 7:
            mod_list = [1, 3, 2, 6, 4, 5] # 10**i
            if n == 1:
                return "7"
            elif n == 2:
                return "77"

            if n % 2 == 1:
                a = n // 2
                # print((mod_list[n%6], mod_list[(a)%6]))
                for sub in range(9, 0, -1):
                    mod = mod_list[n%6] - 1 - mod_list[(a)%6] * (9 - sub)
                    if mod % 7 == 0:
                        return "9"*(a) + str(sub) + "9"*(a)
            else:
                a = n // 2
                for sub in range(9, 0, -1):
                    mod = mod_list[n%6] - 1 - (mod_list[(a-1)%6] + mod_list[a%6]) * (9 - sub)
                    if mod % 7 == 0:
                        return "9"*(a-1) + str(sub) * 2 + "9"*(a-1)
            # elif n % 6 == 3:
            #     foo = n // 6
            #     s 
            #     return "9" * (n//2) + "6" + "9" * (n//2)
            # elif n % 6 == 4:
            #     return "9" * (n//2-1) + "77" + "9" * (n//2-1)
            # elif n % 6 == 5:
            #     return "9" * (n//2) + "7" + "9" * (n//2)
            # elif n % 6 == 0:
            #     return "9" * n
            # elif n % 6 == 1:
            #     return "9" * (n//2) + "4" + "9" * (n//2)
            # elif n % 6 == 2:
            #     return "9" * (n//2-1) + "44" + "9" * (n//2-1)
        if k == 8:
            if n <= 6:
                return "8" * n
            else:
                return "888" + "9" * (n-6) + "888"
