class Solution {
public:

bool isDivisibleBy7(const string& num) {
    int remainder = 0;
    for (char digit : num) 
    {
        remainder = (remainder * 10 + (digit - '0')) % 7;
    }
    return remainder == 0;
}
    string largestPalindrome(int n, int k) {
        string ans="";
        if (k==1 || k==3 || k==9)
        {
            for (int i=0; i<n; i++)
            {
                ans+='9';
            }
        }
        else if (k==2)
        {
            if (n==1)
            {
                ans+='8';
            }
            else
            {
                ans+='8';
                for (int i=1; i<n-1; i++)
                {
                    ans+='9';
                }
                ans+='8';
            }
        }
        else if (k==4)
        {
            if (n<=4)
            {
                for (int i=0; i<n; i++)
                {
                    ans+='8';
                }
            }
            else
            {
                ans+="88";
                for (int i=0; i<n-4; i++)
                {
                    ans+='9';
                }
                ans+="88";
            }
        }
        else if (k==5)
        {
            if (n==1)
            {
                ans+='5';
            }
            else
            {
