                ans+='8';
                for (int i=0; i<n/2-1; i++)
                {
                    ans+='9';
                }
                ans+='8';
                for (int i=0; i<n/2-1; i++)
                {
                    ans+='9';
                }
                ans+='8';
            }
            else
            {
                ans+='8';
                for (int i=0; i<n/2-2; i++)
                {
                    ans+='9';
                }
                ans+="77";
                for (int i=0; i<n/2-2; i++)
                {
                    ans+='9';
                }
                ans+='8';
            }
        }
        else if (k==7)
        {
            if (n==1)
            {
                ans+='7';
            }
            else if (n==2)
            {
                ans+="77";
            }
            else
            {
                if (n%2==1)
                {
                    for (int i=9; i>=0; i--)
                    {
                        ans="";
                        for (int i=0; i<n/2; i++)
                        {
                            ans+='9';
                        }
                        ans+=to_string(i);
                        for (int i=0; i<n/2; i++)
                        {
                            ans+='9';
                        }
                        if (isDivisibleBy7(ans)==true)
                        {
                            break;
                        }
                    }
                }
                else
                {
                    for (int i=9; i>=0; i--)
                    {
                        ans="";
                        for (int i=0; i<n/2-1; i++)
                        {
                            ans+='9';
                        }
                        ans+=to_string(i)+to_string(i);
                        for (int i=0; i<n/2-1; i++)
                        {
                            ans+='9';
                        }
                        if (isDivisibleBy7(ans)==true)
                        {
                            break;
                        }
                    }
                }
            }
        }
        else if (k==8)
        {
            if (n<=6)
            {
                for (int i=0; i<n; i++)
                {
                    ans+='8';
                }
            }
            else
            {
                ans+="888";
                for (int i=0; i<n-6; i++)
                {
                    ans+='9';
                }
