class Solution {
public:
    bool valid(string& s){
        // cout<<s<<endl;
        if(s.length()<9){
            return (stoi(s)%7==0);
        }
        int last = s.back()-'0';
        s.pop_back();
        int last2 = (s.back()-'0')+10*(s[s.length()-2]-'0');
        last2-=last*2;
        s[s.length()-2] = char((last2/10)%10+'0');
        s[s.length()-1] = char(last2%10+'0');
        return valid(s);
    }
    string largestPalindrome(int n, int k) {
        if(k==1 || k==3){
            string s = "";
            for(int i=0;i<n;i++) s.push_back('9');
            return s;
        }
        if(k==2){
            if(n<2){
                return "8";
            }
            string s = "";
            s.push_back('8');
            for(int i=0;i<n-2;i++) s.push_back('9');
            s.push_back('8');
            return s;
        }
        if(k==4){
            if(n<4){
                string s = "";
                for(int i=0;i<n;i++) s.push_back('8');
                return s;
            }
            string s = "";
            s.push_back('8');
            s.push_back('8');
            for(int i=0;i<n-4;i++) s.push_back('9');
            s.push_back('8');
            s.push_back('8');
            return s;

        }
        if(k==5){
            if(n<2){
                return "5";
            }
            string s = "";
            s.push_back('5');
            for(int i=0;i<n-2;i++) s.push_back('9');
            s.push_back('5');
            return s;
        }
        if(k==6){
            if(n<6){
                switch(n){
                    case 1:
                    return "6";
                    case 2:
                    return "66";
                    case 3:
                    return "888";
                    case 4:
                    return "8778";
                    case 5:
                    return "89898";
                }
            }
            string s = "8";
            for(int i=0;i<n-2;i++){
                s.push_back('9');
            }
            s.push_back('8');
            if(n%2){
                s[n/2] = '8';
            }else{
                s[n/2] = '7';
                s[n/2-1] = '7';
            }
            return s;
        }
        if(k==7){
            if(n%2==1){
                string s = largestPalindrome(n,1);
                for(int i=9;i>2;i--){
                    // cout<<char(i)<<endl;
                    s[s.length()/2] = char('0'+i);
                    string str = s;
                    if(valid(str)){
                        return s;
                    }
                }
            }
            if(n%2==0){
                string s = largestPalindrome(n,1);
                for(int i=9;i>2;i--){
                    s[s.length()/2] = char(i+'0');
                    s[s.length()/2-1] = char(i+'0');
                    string str = s;
                    if(valid(str)){
                // cout<<s<<endl;
                        return s;
                    }
                }
            }
        }
        if(k==8){
            if(n<6){
                string s = "";
                for(int i=0;i<n;i++) s.push_back('8');
                return s;
            }
            string s = "888";
            for(int i=0;i<n-6;i++) s.push_back('9');
            s.push_back('8');
            s.push_back('8');
            s.push_back('8');
            return s;
        }
        if(k==9){
            return largestPalindrome(n,3);
        }
        return "";
    }
};
