import java.math.BigInteger;
class Solution {
    public String largestPalindrome(int n, int k) {
        if(k==1 || k==3 || k==9){
            StringBuilder sb = new StringBuilder();
            for(int i =0;i<n;i++){
                sb.append(9);
            }
            return sb.toString();
        }
        if(k==2){
            if(n==1){
                return "8";
            }
            StringBuilder sb = new StringBuilder();
            for(int i =0;i<n;i++){
                sb.append(9);
            }
            sb.setCharAt(0,'8');
            sb.setCharAt(sb.length()-1,'8');
            return sb.toString();
        }
        if(k==4){
            if(n==1){
                return "8";
            }
            if(n==2){
                return "88";
            }
            if(n==3){
                return "888";
            }
            if(n==4){
                return "8888";
            }
            StringBuilder sb = new StringBuilder();
            for(int i =0;i<n;i++){
                sb.append(9);
            }
            sb.setCharAt(0,'8');
            sb.setCharAt(sb.length()-1,'8');
            sb.setCharAt(sb.length()-2,'8');
            sb.setCharAt(1,'8');
            return sb.toString();
        }
        if(k==8){
            if(n==1){
                return "8";
            }
            if(n==2){
                return "88";
            }
            if(n==3){
                return "888";
            }
            if(n==4){
                return "8888";
            }
            StringBuilder sb = new StringBuilder();
            sb.append("9".repeat(Math.max(0, n)));
            sb.setCharAt(sb.length()-1,'8');
            sb.setCharAt(sb.length()-2,'8');
            sb.setCharAt(sb.length()-3,'8');
            sb.setCharAt(1,'8');
            sb.setCharAt(0,'8');
            sb.setCharAt(2,'8');
            return sb.toString();
        }
        if(k==5){
            if(n==1){
                return "5";
            }
            StringBuilder sb = new StringBuilder();
            sb.append("9".repeat(Math.max(0, n)));
            sb.setCharAt(0,'5');
            sb.setCharAt(sb.length()-1,'5');
            return sb.toString();
        }
        if(k==6){
            if(n==1){
                return "6";
            }
            if(n==2){
                return "66";
            }
            StringBuilder sb = new StringBuilder();
            if(n%2==0){
                sb.append("9".repeat(Math.max(0, n)));
                sb.setCharAt(0,'8');
                sb.setCharAt(sb.length()-1,'8');
                sb.setCharAt(sb.length()/2-1,'7');
                sb.setCharAt(sb.length()/2,'7');
            }else{
                sb.append("9".repeat(Math.max(0, n)));
                sb.setCharAt(0,'8');
                sb.setCharAt(sb.length()-1,'8');
                sb.setCharAt(sb.length()/2,'8');
            }
            return sb.toString();
        }
        if(k==7){
            if(n==1){
                return "7";
            }
            if(n==2){
                return "77";
            }
            StringBuilder sb = new StringBuilder();
            sb.append("9".repeat(Math.max(0, n)));
            if(n%2==0){
                for(int i = 9;i>0;i--){
                    sb.setCharAt(sb.length()/2-1, (char) ('0'+i));
                    sb.setCharAt(sb.length()/2, (char) ('0'+i));
                    BigInteger num = new BigInteger(sb.toString());
                    BigInteger div = new BigInteger("7");
                    BigInteger remainder = num.mod(div);

                    // 判断余数是否为0
                    if (remainder.equals(BigInteger.ZERO)) {
                        return sb.toString();
                    }
                }
            }else{
                for(int i = 9;i>0;i--){
                    sb.setCharAt(sb.length()/2, (char) ('0'+i));
                    BigInteger num = new BigInteger(sb.toString());
                    BigInteger div = new BigInteger("7");
                    BigInteger remainder = num.mod(div);
                    // 判断余数是否为0
                    if (remainder.equals(BigInteger.ZERO)) {
                        return sb.toString();
                    }
                }
            }

        }
        return "";
    }
}
