class Solution {

    public boolean isDivisibleBy7(String largeNumber) {
        int remainder = 0;

        for (char digit : largeNumber.toCharArray()) {
            remainder = (remainder * 10 + (digit - '0')) % 7;
        }

        return remainder == 0;
    }

    public boolean check(String s) {
        for (int p = 0; p < s.length() / 2; p++) {
            if (s.charAt(p) != s.charAt(s.length() - 1 - p)) return false;
        }
        return true;
    }

    public String largestPalindrome(int n, int k) {
        StringBuilder s = new StringBuilder();

        if (k == 1) {
            for (int p = 0; p < n; p++) s.append('9');
        } else if (k == 2) {
            if (n == 1) s.append('8');
            else {
                s.append('8');
                for (int p = 1; p < n - 1; p++) {
                    s.append('9');
                }
                s.append('8');
            }
        } else if (k == 3) {
            for (int p = 0; p < n; p++) s.append('9');
        } else if (k == 4) {
            if (n == 1) {
                s.append('8');
            } else if (n == 2) {
                s.append('8').append('8');
            } else if (n == 3) {
                s.append('8').append('8').append('8');
            } else {
                s.append('8').append('8');
                for (int p = 2; p < n - 2; p++) s.append('9');
                s.append('8').append('8');
            }
        } else if (k == 5) {
            if (n == 1) {
                s.append('5');
            } else {
                s.append('5');
                for (int p = 1; p < n - 1; p++) s.append('9');
                s.append('5');
            }
        } else if (k == 6) {
            if (n == 1) {
                s.append('6');
            } else if (n == 2) {
                s.append('6').append('6');
            } else if (n == 3) {
                s.append('8').append('8').append('8');
            } else {
                for (int p = 0; p < n; p++) s.append('9');
                s.setCharAt(0, '8');
                s.setCharAt(n - 1, '8');
                if (n % 2 == 1) {
                    s.setCharAt(n / 2, '8');
                } else {
                    s.setCharAt(n / 2, '7');
                    s.setCharAt(n / 2 - 1, '7');
                }
            }
        } else if (k == 7) {
            for (int p = 0; p < n; p++) s.append('9');
            if (n % 2 == 1) {
                for (char p = '9'; p >= '0'; p--) {
                    s.setCharAt(n / 2, p);
                    if (isDivisibleBy7(s.toString())) {
                        break;
                    }
                }
            } else {
                for (char p = '9'; p >= '0'; p--) {
                    s.setCharAt(n / 2, p);
                    s.setCharAt(n / 2 - 1, p);
                    if (isDivisibleBy7(s.toString())) {
                        break;
                    }
                }
            }
        } else if (k == 8) {
            if (n == 1) {
                s.append('8');
            } else if (n == 2) {
                s.append('8').append('8');
            } else if (n == 3) {
                s.append('8').append('8').append('8');
            } else if (n == 4) {
                s.append('8').append('8').append('8').append('8');
            } else if (n == 5) {
                s.append('8').append('8').append('8').append('8').append('8');
            } else if (n == 6) {
                s.append('8').append('8').append('8').append('8').append('8').append('8');
            } else {
                s.append('8');
                s.append('8');
                s.append('8');
                for (int p = 3; p < n - 3; p++) s.append('9');
                s.append('8');
                s.append('8');
                s.append('8');
            }
        } else {
            for (int p = 0; p < n; p++) s.append('9');
        }
        return s.toString();
    }
}
