class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        # 1, 3, 9: 99...99
        if k == 1 or k == 3 or k == 9:
            return '9' * n
        # 2: 89...98
        if k == 2:
            c = ['9'] * n
            c[0] = '8'
            c[-1] = '8'
            return ''.join(c)
        # 4: 889...988
        if k == 4:
            c = ['9'] * n
            c[0] = '8'
            c[-1] = '8'
            if n >= 2:
                c[1] = '8'
                c[-2] = '8'
            return ''.join(c)
        # 5: 59..95
        if k == 5:
            c = ['9'] * n
            c[0] = '5'
            c[-1] = '5'
            return ''.join(c)
        # 8: 8889..9888
        if k == 8:
            c = ['9'] * n
            c[0] = '8'
            c[-1] = '8'
            if n >= 2:
                c[1] = '8'
                c[-2] = '8'
            if n >= 3:
                c[2] = '8'
                c[-3] = '8'
            return ''.join(c)
        # 7: 99..x..99
        if k == 7:
            if n <= 2:
                return '7' * n
            full = (pow(10, n, 7) - 1) % 7
            if n % 2 == 0:
                place_value = pow(10, n//2-1, 7) + pow(10, n//2, 7)
            else:
                place_value = pow(10, n//2, 7)
            place_value = place_value % 7
            inner = 9
            while full % 7 != 0:
                inner -= 1
                full -= place_value
            c = ["9"] * n
            c[n//2] = str(inner)
            if n % 2 == 0: 
                c[n//2-1] = str(inner)
            return "".join(c)

        # 6: 89..x(77/8)..98
        if k == 6:
            if n == 1:
                return '6'
            if n == 2:
                return '66'
            if n == 3:
                return '888'
            c = ['9'] * n
            c[0] = '8'
            c[-1] = '8'
            if n % 2 == 0:
                c[n//2] = '7'
                c[n//2-1] = '7'
            else:
                c[n//2] = '8'
            return ''.join(c)
