class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k==1:
            return '9' * n
        elif k==2:
            if n==1:
                return "8"
            elif n==2:
                return "88"
            else:
                return '8' + '9' * (n-2) + '8'
        elif k==3:
            return '9' * n
        elif k==4:
            if n==1:
                return "8"
            elif n==2:
                return "88"
            elif n==3:
                return "888"
            else:
                return "88" + '9' * (n-4) + "88"
        elif k==5:
            if n==1:
                return "5"
            elif n==2:
                return "55"
            else:
                return '5' + '9' *(n-2) +'5'
        elif k==6:
            if n==1:
                return "6"
            elif n==2:
                return "66"
            else:
                if n%2 ==1:
                    return '8' +'9' *((n-3)//2) + '8' + '9' *((n-3)//2) + '8'
                else:
                    return '8' +'9' *((n-4)//2) + "77" + '9' *((n-4)//2) + '8'
        elif k==7:
            ls = ["", "7", "77", "959", "9779", "99799", "999999", "9994999", "99944999", "999969999", "9999449999", "99999499999"]
            k = n % 12
            p = n//12
            return "999999" * p + ls[k] + "999999" * p

        elif k==8:
            if n <=6:
                return '8' * n
            else:
                return "888" + '9' * (n-6) + "888"
        elif k==9:
            return '9' * n
