class Solution {
public:
    string largestPalindrome(int n, int k) {
        if(k==1 || k==3 || k==9)
        {
            string s(n,'9');
            return s;
        }
        if(k==2)
        {
            string s(n,'9');
            s[0]='8';
            s[n-1]='8';
            return s;
        }
        if(k==5)
        {
            string s(n,'9');
            s[0]='5';
            s[n-1]='5';
            return s;
        }
        if(k==4)
        {
            string s(n,'8');
            for(int i=2;i<n-2;i++)
                s[i]='9';
            return s;
        }
        if(k==6)
        {
            if(n==1)
                return "6";
            if(n==2)
                return "66";
            string s(n,'9');
            s[0]='8';
            s[n-1]='8';
            if(n%2)
            {
                s[n/2]='8';
            }
            else
            {
                s[n/2]='7';
                s[n/2-1]='7';
            }
            return s;
        }
        if(k==7)
        {
            string s(n,'9');
            if(n%2)
            {
                int a[6]={7,5,7,4,6,4};
                int r=(n%12)/2;
                s[n/2]=char('0'+a[r]);
            }
            else
            {
                int a[6]={7,7,9,4,4,9};
                int r=((n-2)%12)/2;
                s[n/2]=char('0'+a[r]);
                s[n/2-1]=char('0'+a[r]);
            }
            cout<<s<<"\n";
            return s;
        }
        if(k==8)
        {
            string s(n,'8');
            for(int i=3;i<n-3;i++)
                s[i]='9';
            return s;
        }

        return "0";
    }
};
