class Solution {
public:
    int check(string s,int n,int k)
    {
        int mod = 0;
        for(int i =n-1;i>=0;i--)
        {
            mod= (mod*10+(s[i]-'0'))%k;
        }
        return mod==0;
    }
    string largestPalindrome(int n, int k) 
    {
        if(k==5)
        {
            int i = 1,j=n-2;
            string s(n,'0');
               s[0]='5',s[n-1]='5';
               while(i<=j)s[i++]='9',s[j--]='9';

               return s;
        }
        else if(k%2)
        {
            int i = 0,j=n-1;
            string s(n,'0');

            while(i+1<j)
                s[i++]='9',s[j--]='9';

            for(char ch = '9';ch>='0';ch--)
            {
                s[i]=ch,s[j]=ch;
                if(check(s,n,k)) return s;
            }

        }   
        else if(k==2 or k==6)
        {
            if(k==8 and n<7)
            {
                string s(n,'0');
                for(int i =0;i<n;i++)
                {
                    s[i]='8';
                }
                return s;
            }

            if(n==1){
                if(k!=6)    
                    return "8";
                return "6";
            } 
            if(n==2){
                if(k!=6)    
                    return "88";
                return "66";
            }
            string s(n,'0');
            s[0]='8',s[n-1]='8';
            int i =1,j=n-2;
            while(i+1<j)s[i++]='9',s[j--]='9';

            for(char ch = '9';ch>='0';ch--)
            {
                s[i]=ch,s[j]=ch;
                if(check(s,n,k)) return s;
            }                   
        } 
        else if(k==4)
        {
            if(k==4 and n<5)
            {
                string s(n,'0');
                for(int i =0;i<n;i++)
                {
                    s[i]='8';
                }
                return s;
            }
            string s(n,'0');
            s[0]='8',s[n-1]='8';
            s[1]='8',s[n-2]='8';
            int i =2,j=n-3;
            while(i+1<j)s[i++]='9',s[j--]='9';

            for(char ch = '9';ch>='0';ch--)
            {
                s[i]=ch,s[j]=ch;
                if(check(s,n,k)) return s;
            }                   
        }
        else if(k==8)
        {
            if(k==8 and n<7)
            {
                string s(n,'0');
                for(int i =0;i<n;i++)
                {
                    s[i]='8';
                }
                return s;
            }
            string s(n,'0');
            s[0]='8',s[n-1]='8';
            s[1]='8',s[n-2]='8';
            s[2]='8',s[n-3]='8';
            int i =3,j=n-4;
            while(i+1<j)s[i++]='9',s[j--]='9';

            for(char ch = '9';ch>='0';ch--)
            {
                s[i]=ch,s[j]=ch;
                if(check(s,n,k)) return s;
            }  
        }

        return "";
    }
};
