class Solution {
public:
    bool check(string& a) {
        int k = 0;
        for(int i = 0;i < a.length();i++) {
            int b = k*10 + a[i] - '0';
            k = b % 7;
        }
        return k == 0;
    }

    string largestPalindrome(int n, int k) {
        string ans = "";
        for(int i = 0;i < n;i++) {
            ans.push_back('9');
        }
        if(k == 1 || k == 3 || k == 9)
            return ans;
        if(k == 2) {
            ans[0] = '8';
            ans[n-1] = '8';
            return ans;
        }
        if(k == 4) {
            if(n < 2)
                return "8";
            ans[0] = '8';
            ans[1] = '8';
            ans[n-1] = '8';
            ans[n-2] = '8';
            return ans;
        }
        if(k == 5) {
            ans[0] = '5';
            ans[n-1] = '5';
            return ans;
        }
        if(k == 8) {
            if(n == 1)
                return "8";
            if(n == 2)
                return "88";
            for(int i = 0;i < 3;i++) {
                ans[i] = '8';
                ans[n-i-1] = '8';
            }
            return ans;
        }
        if(k == 6) {
            if(n == 1)
                return "6";
            if(n == 2)
                return "66";
            // if(n == 4) {
            //     return "8778";
            // }
            if(n % 2) {
                ans[0] = '8';
                ans[n-1] = '8';
                ans[n/2] = '8';
                return ans;
            }
            ans[0] = '8';
            ans[n-1] = '8';
            int m = n/2;
            ans[m] = '7';
            // ans[m+1] = '8';
            ans[m-1] = '7';
            // ans[m-2] = '8';
            // ans = "";
            // for(int i = 0;i < n;i++) {
            //     ans.push_back('8');
            // }
            return ans;
        }
        if(n % 2) {
            int m = n/2;
            for(int i = 9;i >= 0;i--) {
                ans[m] = '0' + i;
                if(check(ans))
                    return ans;
            }
        }
        for(int i = 9;i >= 0;i--) {
            int m = n/2;
            ans[m] = '0' + i;
            ans[m-1] = '0' + i;
            if(check(ans))
                return ans;
        }

        return ans;
    }
};
