import java.math.BigInteger;

class Solution {
    public String largestPalindrome(int n, int k) {
        StringBuilder ret = new StringBuilder();
        if (k == 1 || k == 3 || k == 9) {
            for (int i = 0; i < n; i++) ret.append(9);            
        }
        else if (k == 2) {
            if (n == 1) return "8";
            else if (n == 2) return "88";
            ret.append(8);
            for (int i = 0; i < n - 2; i++) ret.append(9);
            ret.append(8);
        }
        else if (k == 4) {
            if (n == 1) return "8";
            else if (n == 2) return "88";
            else if (n == 3) return "888";            
            else if (n == 4) return "8888";
            ret.append(88);
            for (int i = 0; i < n - 4; i++) ret.append(9);
            ret.append(88);
        }
        else if (k == 5) {
            if (n == 1) return "5";
            else if (n == 2) return "55";
            ret.append(5);
            for (int i = 0; i < n - 2; i++) ret.append(9);
            ret.append(5);
        }
        else if (k == 6) {
            if (n == 1) return "6";
            else if (n == 2) return "66";
            else if (n == 3) return "888";
            else if (n == 4) return "8778";
            else {
                if (n % 2 == 1) {
                    ret.append(8);
                    for (int i = 0; i < n / 2 - 1; i++) ret.append(9);
                    ret.append(8);
                    for (int i = 0; i < n / 2 - 1; i++) ret.append(9);
                    ret.append(8);
                }
                else {
                    ret.append(8);
                    for (int i = 0; i < n / 2 - 2; i++) ret.append(9);
                    ret.append(7);
                    ret.append(7);
                    for (int i = 0; i < n / 2 - 2; i++) ret.append(9);
                    ret.append(8);
                }                
            }
        }
        else if (k == 7) {
            if (n == 1) return "7";
            else if (n == 2) return "77";
            else {
                if (n % 2 == 1) {
                    StringBuilder pre = new StringBuilder();
                    for (int i = 0; i < n / 2; i++) pre.append(9);


                    for (int i = 9; i >= 0; i--) {
                    	ret = new StringBuilder();
                    	ret.append(pre.toString());
                        ret.append(i);
                        ret.append(pre.toString());

                        BigInteger bg = new BigInteger(ret.toString());
                        BigInteger seven = new BigInteger("7");
                        if (bg.mod(seven).intValue() == 0) {
                        	return ret.toString();
                        }                                            
                    }                    
                }
                else {
                    StringBuilder pre = new StringBuilder();
                    for (int i = 0; i < n / 2 - 1; i++) pre.append(9);


                    for (int i = 9; i >= 0; i--) {
                    	ret = new StringBuilder();
                    	ret.append(pre.toString());
                        ret.append(i);
                        ret.append(i);
                        ret.append(pre.toString());

                        BigInteger bg = new BigInteger(ret.toString());
                        BigInteger seven = new BigInteger("7");
                        if (bg.mod(seven).intValue() == 0) {
                        	return ret.toString();
                        }                                            
                    }                   
                }
            }
        }
        else if (k == 8) {
            if (n < 7) {
                for (int i = 0; i < n; i++) ret.append(8);
            }
            else {
                ret.append(888);
                for (int i = 0; i < n - 6; i++) ret.append(9);
                ret.append(888);
            }
        }

        return ret.toString();
    }
}
