class Solution {
    bool isDivisibleBy7(const string& numStr) {
        long long remainder = 0;
        const int MOD = 7;

        for (char digit : numStr) {
            int digitValue = digit - '0';
            remainder = (remainder * 10 + digitValue) % MOD;
        }

        return remainder == 0;
    }

public:
    string largestPalindrome(int n, int k) {
        if (k == 1 || k == 3 || k == 9) {
            return string(n, '9');
        } else if (k == 2) {
            string s(n, '9');
            s[0] = '8';
            s[n - 1] = '8';
            return s;
        } else if (k == 4) {
            string s(n, '8');
            for(int i = 2 ; i < n - 2; i++){
                s[i] = '9';
            }
            return s;
        } else if (k == 5) {
            string s(n, '9');
            s[0] = '5';
            s[n - 1] = '5';
            return s;
        } else if (k == 6) {
            if (n == 1 || n == 2) {
                return string(n, '6');
            }
            vector<char> s(n, '9');

            s[0] = '8';
            s[n - 1] = '8';

            if (n % 2 == 1) {
                int midIndex = n / 2;
                s[midIndex] = '9' - 1;
            } else {
                int midIndex1 = n / 2 - 1;
                int midIndex2 = n / 2;
                char midChar = '9' - 2;
                s[midIndex1] = midChar;
                s[midIndex2] = midChar;
            }

            return string(s.begin(), s.end());
        } else if (k == 7) {
            if (n == 1 || n == 2) {
                return string(n, '7');
            }
            string s(n, '9');
            for (int i = 0; i < 10; i++) {
                if (n % 2 == 0) {
                    int mid = n / 2;
                    s[mid-1] = '9' - i;
                    s[mid] = '9' - i;
                    if (isDivisibleBy7(s)) {
                        return s;
                    }
                } else {
                    int mid = n / 2;
                    s[mid] = '9' - i;
                    if (isDivisibleBy7(s)) {
                        return s;
                    }
                }
            }
        } else if (k == 8) {
            string s(n, '8');
            for(int i = 3 ; i < n - 3; i++){
                s[i] = '9';
            }
            return s;
        }

        return "1";
    }
};
