class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k == 7:
            if n <= 5:
                output = ["7", "77", "959", "9779", "99799"]
                return output[n - 1]
            else:
                ans = 0
                for i in range(n):
                    if i >= (n - 1) // 2 and i <= n // 2:
                        ans = ans * 10
                    else:
                        ans = ans * 10 + 9
                # print(ans)
                base = int(pow(10, (n - 1) // 2))
                # print(base)
                for i in reversed(range(10)):
                    if n % 2 == 0:
                        ii = i * 10 + i
                    else:
                        ii = i
                    temp = ans + base * ii
                    if temp % 7 == 0:
                        sys.set_int_max_str_digits(1000000)
                        return str(temp)
        if k in [1, 9, 3]:
            return "9" * n
        if k == 2:
            output = ["8", "88"]
            if n < 3:
                return output[n - 1]
            else:
                return "8" + "9" * (n - 2) + "8"
        if k == 4:
            output = ["8", "88", "888", "8888"]
            if n < 5:
                return output[n - 1]
            else:
                return "88" + "9" * (n - 4) + "88"
        if k == 8:
            if n < 7:
                return "8" * n
            else:
                return "888" + "9" * (n - 6) + "888"
        if k == 5:
            if n < 3:
                return "5" * n
            else:
                return "5" + "9" * (n - 2) + "5"
        if k == 6:
            if n < 6:
                output = ["6", "66", "888", "8778", "89898"]
                return output[n - 1]
            else:
                if n % 2 == 0:
                    return "8" + "9" * (n // 2 - 2) + "77" + "9" * (n // 2 - 2) + "8"
                else:
                    return "8" + "9" * (n // 2 - 1) + "8" + "9" * (n // 2 - 1) + "8"

        return ""
