class Solution {
public:
    bool f(int x){
        int c = 0;
        int r = x;
        while(x){
            c = c * 10 + x % 10;
            x /= 10;
        }
        return c == r;
    }
    int test(int n, int k){
        int s = 1;
        while(n--) s *= 10;
        for(int i = s-1; i > s/10; --i){
            if(f(i) && i % k == 0) return i;
        }
        return -1;
    }
    string largestPalindrome(int n, int k) {

        // 5, 6, 7
        string ans(n, '0' + k);

        if(k == 9) return ans;
        if(k == 8){
            if(n <= 6) return ans;
            for(int i = 0; i < n - 6; ++ i) ans[i+3] = '9';
            return ans;
        }
        if(k == 7){
            if(n == 1 || n == 2) return ans;
            if(n % 6 == 0) return string(n, '9');
            int c1 = (n / 6) & 1;
            int c2 = n % 6;
            if(c1){
                ans = string(n, '9');
                if(c2 == 1 || c2 == 5) ans[(n-1)/2] = '4';
                else if(c2 == 3) ans[(n-1)/2] = '6';
                else ans[(n-1)/2] = '4', ans[(n-1)/2+1] = '4';
                return ans;
            }else{
                ans = string(n, '9');
                if(c2 == 1 || c2 == 5) ans[(n-1)/2] = '7';
                else if(c2 == 3) ans[(n-1)/2] = '5';
                else ans[(n-1)/2] = '7', ans[(n-1)/2+1] = '7';
                return ans;
            }
        }
        if(k == 6){
            if(n == 1 || n == 2) return ans;
            if(n == 3) return "888";
            ans = string(n, '9');
            ans[0] = ans[n-1] = '8';
            if(n & 1) ans[(n-1)/2] = '8';
            else ans[(n-1)/2] = '7', ans[(n-1)/2+1] = '7';
            return ans;
        }
        if(k == 5){
            if(n == 1 || n == 2) return ans;
            ans = string(n, '9');
            ans[0] = ans[n-1] = '5';
            return ans;
        }
        if(k == 4){
            ans = string(n, '8');
            if(n <= 4) return ans;
            for(int i = 0; i < n - 4; ++i) ans[i+2] = '9';
            return ans;
        }
        if(k == 3 || k == 1){
            ans = string (n, '9');
            return ans;
        }
        if(k == 2){
            ans = string(n, '8');
            if(n <= 2) return ans;
            for(int i = 0; i < n - 2; ++i) ans[i+1] = '9';
            return ans;
        }
        return "";
    }
};
