class Solution {
public:
    bool f(string &t , int x){
        int rem = 0;
        int n = t.size();
        for(int i = 0 ; i < n ; i ++){
            int num = (t[i] - '0') + (10 * rem);
            rem = num % x;
        }
        return (rem == 0);
    }
    string largestPalindrome(int n, int k) {
        if(k == 1 || k == 3 || k == 9){
            return string(n , '9');
        }
        if(k == 2){
            if(n == 1){
                return "8";
            }
            if(n == 2){
                return "88";
            }
            return ('8' + string(n - 2, '9') + '8');
        }
        if(k == 4){
            if(n <= 4){
                return string(n , '8');
            }
            return ("88" + string(n - 4 , '9') + "88");
        }
        if(k == 5){
            if(n <= 2){
                return string(n , '5');
            }
            return ('5' + string(n - 2 , '9') + '5');
        }
        if(k == 6){
            if(n <= 2){
                return string(n , '6');
            }
            if(n == 3){
                return "888";
            }
            if(n % 2 == 0){
                return ('8' + string((n - 4) / 2 , '9') + "77" + string((n - 4) / 2 , '9') + '8');
            }
            return ('8' + string((n - 3) / 2 , '9') + '8' + string((n - 3) / 2 , '9') + '8');
        }
        if(k == 7){
            if(n <= 2){
                return string(n , '7');
            }
            if(n % 2){
                for(char i = '9' ; i >= '0' ; i --){
                    string temp = string(n , '9');
                    temp[n / 2] = i;
                    if(f(temp , 7)){
                        return temp;
                    }
                }
            }
            else{
                string temp = string(n , '9');
                for(char i = '9' ; i >= '0' ; i --){
                    string temp = string(n , '9');
                    temp[n / 2] = i;
                    temp[n / 2 - 1] = i;
                    if(f(temp , 7)){
                        return temp;
                    }
                }
            }
        }
        if(k == 8){
            if(n <= 2){
                return string(n , '8');
            }
            if(n % 2 && n <= 6){
                for(char i = '9' ; i >= '0' ; i --){
                    string temp = string(n , '8');
                    temp[n / 2] = i;
                    if(f(temp , 8)){
                        return temp;
                    }
                }
            }
            else if(n <= 6){
                string temp = string(n , '9');
                for(char i = '9' ; i >= '0' ; i --){
                    string temp = string(n , '8');
                    temp[n / 2] = i;
                    temp[n / 2 - 1] = i;
                    if(f(temp , 8)){
                        return temp;
                    }
                }
            }
            return ("888" + string(n - 6 , '9') + "888");
        }
        return "q3";
    }
};
