class Solution(object):
    def largestPalindrome(self, n, k):
        """
        :type n: int
        :type k: int
        :rtype: str
        """

        # for i in range(1000):
        #     print(7*i)
        # return 0

        res = []
        if k == 1 or k == 3 or k == 9:
            for _ in range(n):
                res.append('9')
            return ''.join(res)
        if k == 2:
            if n == 1:
                return '8'
            res.append('8')
            for _ in range(n-2):
                res.append('9')
            res.append('8')
            return ''.join(res)
        if k == 4:
            if n == 1:
                return '8'
            if n == 2:
                return "88"
            if n == 3:
                return "888"
            res.append('8')
            res.append('8')
            for _ in range(n-4):
                res.append('9')
            res.append('8')
            res.append('8')
            return ''.join(res)
        if k == 5:
            if n == 1:
                return '5'
            res.append('5')
            for _ in range(n-2):
                res.append('9')
            res.append('5')
            return ''.join(res)
        if k == 6:
            if n == 1:
                return '6'
            if n == 2:
                return '66'
            if n == 3:
                return '888'
            if n == 4:
                return "8778"
            if n % 2 == 1:
                res.append('8')
                for _ in range((n-3)/2):
                    res.append('9')
                res.append('8')
                for _ in range((n-3)/2):
                    res.append('9')
                res.append('8')
                return ''.join(res)
            res.append('8')
            for _ in range((n-4)/2):
                res.append('9')
            res.append('7')
            res.append('7')
            for _ in range((n-4)/2):
                res.append('9')
            res.append('8')
            return ''.join(res)
        if k == 7:
            if n == 1:
                return '7'
            if n == 2:
                return "77"
            if n % 2 == 1:
                i = 0
                while i < 9:
                    temp = int("9"*n) - i*10**(n//2)
                    if temp % 7 == 0:
                        break
                    i += 1
                for _ in range((n-1)/2):
                    res.append('9')
                res.append(str(9-i))
                for _ in range((n-1)/2):
                    res.append('9')
                return ''.join(res)
            i = 0
            while i < 9:
                temp = int("9"*n) - i*10**(n/2) - i*10**(n/2-1)
                if temp % 7 == 0:
                    break
                i += 1
            for _ in range((n-1)/2):
                res.append('9')
            res.append(str(9-i))
            res.append(str(9-i))
            for _ in range((n-1)/2):
                res.append('9')
            return ''.join(res)

        if k == 8:
            if n <= 5:
                for _ in range(n):
                    res.append('8')
                return ''.join(res)
            res.append('8')
            res.append('8')
            res.append('8')
            for _ in range(n-6):
                res.append('9')
            res.append('8')
            res.append('8')
            res.append('8')
            return ''.join(res)
