class Solution {
public:
    string largestPalindrome(int n, int k) {
        string s="";
        if(k==1 || k==3 || k==9){
            for(int i=0;i<n;i++){
                s+="9";
            }
        }
        else if(k==2){
            s+="8";
            for(int i=1;i<n-1;i++){
                s+="9";
            }
            if(n>=2){
                s+="8";
            }
        }
        else if(k==4){
            if(n<=4){
                for(int i=0;i<n;i++){
                    s+="8";
                }
            }
            else{
                s+="88";
                for(int i=0;i<n-4;i++){
                    s+="9";
                }
                s+="88";
            }
        }
        else if(k==5){
            s+="5";
            for(int i=1;i<n-1;i++){
                s+="9";
            }
            if(n>=2){
                s+="5";
            }
        }
        else if(k==8){
            if(n>6){
                s+="888";
                for(int i=3;i<n-3;i++){
                    s+="9";
                }
                s+="888";
            }
            else{
                for(int i=0;i<n;i++){
                    s+="8";
                }
            }
        }
        else if(k==6){
            if(n<=2){
                for(int i=0;i<n;i++){
                    s+="6";
                }
            }
            else if(n%2==1){
                s+="8";
                for(int i=0;i<(n-3)/2;i++){
                    s+="9";
                }
                s+="8";
                for(int i=0;i<(n-3)/2;i++){
                    s+="9";
                }
                s+="8";
            }
            else{
                s+="8";
                for(int i=0;i<(n-4)/2;i++){
                    s+="9";
                }
                s+="77";
                for(int i=0;i<(n-4)/2;i++){
                    s+="9";
                }
                s+="8";
            }
        }
        else{//k==7
            for(int i=0;i<n;i++){
                s+="9";
            }
            if(n%2==1){
                int cnt=(n-1)/2;
                int m=0;
                for(int i=0;i<cnt;i++){
                    m=(m*10+9)%7;
                }
                int p=m;
                for(int i=0;i<cnt+1;i++){
                    p=(p*10)%7;
                }
                m=(m+p)%7;
                for(int i=9;i>=3;i--){
                    int cur=i;
                    for(int j=0;j<cnt;j++){
                        cur=(cur*10)%7;
                    }
                    if((cur+m)%7==0){
                        s[cnt]=('0'+i);
                        break;
                    }
                }
            }
            else{//n%2==0
                int cnt=(n-2)/2;
                int m=0;
                for(int i=0;i<cnt;i++){
                    m=(m*10+9)%7;
                }
                int p=m;
                for(int i=0;i<cnt+2;i++){
                    p=(p*10)%7;
                }
                m=(m+p)%7;
                for(int i=9;i>=3;i--){
                    int cur=i*11;
                    for(int j=0;j<cnt;j++){
                        cur=(cur*10)%7;
                    }
                    if((cur+m)%7==0){
                        s[cnt]=('0'+i);
                        s[cnt+1]=('0'+i);
                        break;
                    }
                }
            }
        }
        return s;
    }
};
