                else res.append("77");
                for (int i = 0; i < (n - 1) / 2; i++) res.append("9");
                return res.toString();
            }
             else if (tmp == 3) {
                StringBuilder res = new StringBuilder();
                for (int i = 0; i < (n - 1) / 2; i++) res.append("9");
                if ((n & 1) == 1) res.append("5");
                else res.append("55");
                for (int i = 0; i < (n - 1) / 2; i++) res.append("9");
                return res.toString();
            }
             else if (tmp == 7 || tmp == 8 || tmp == 10 || tmp == 11) {
                StringBuilder res = new StringBuilder();
                for (int i = 0; i < (n - 1) / 2; i++) res.append("9");
                if ((n & 1) == 1) res.append("4");
                else res.append("44");
                for (int i = 0; i < (n - 1) / 2; i++) res.append("9");
                return res.toString();
            } else if (tmp == 9) {
