sys.set_int_max_str_digits(1000000000)
class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k==1:
            return '9'*n
        elif k==2:
            if n==1:
                return '8'
            if n==2:
                return '88'
            else:
                return '8'+'9'*(n-2)+'8'
        elif k==3:
            return '9'*n
        elif k==4:
            if n<=4:
                return '8'*n
            else:
                return '88'+'9'*(n-4)+'88'
        elif k==5:
            if n<=2:
                return '5'*n
            else:
                return '5'+'9'*(n-2)+'5'
        elif k==8:
            if n<=6:
                return '8'*n
            else:
                return '8'*3+'9'*(n-6)+'8'*3
        elif k==9:
            return '9'*n
        elif k==6:
            if n<=2:
                return '6'*n
            else:
                if n%2==0:
                    return '8'+'9'*((n-4)//2)+'77'+'9'*((n-4)//2)+'8'
                else:
                    return '8'+'9'*((n-3)//2)+'8'+'9'*((n-3)//2)+'8'
        else:
            max_half = 10**((n + 1) // 2) - 1
            min_half = 10**((n - 1) // 2)
            for half in range(max_half, min_half - 1, -1):
                half_str = str(half)
                if n%2==0:
                    palindrome = half_str + half_str[::-1]
                    p=int(palindrome)
                    if p % k == 0:
                        return palindrome
                else:
                    palindrome = half_str + half_str[-2::-1]
                    p=int(palindrome)
                    if p % k == 0:
                        return palindrome
        return ""
