class Solution {
public:
    bool check(string s){
        string s1 = "";
        int sum = 0;
        int c = 0;
        bool flag = 0;
        int n = s.length();
        for(int i = n - 1; i >= 0; i--){
            s1.push_back(s[i]);
            c++;
            if(c % 3 == 0){
                c = 0;
                reverse(s1.begin(), s1.end());
                int x = stoi(s1);
                s1 = "";
                if(flag){
                    sum -= x;
                } 
                else{
                    sum += x;
                }
                flag = !flag;
            }
        }
        if( c!=0){
            int x = stoi(s1);
                if(flag){
                    sum -= x;
                } 
                else{
                    sum += x;
                }
        }
        if(sum % 7 == 0){
            return true;
        }
        return false;
    }
    string largestPalindrome(int n, int k) {
        if(n == 1 && k == 6){
            return "6";
        }
        string s = "";
        if(k == 1 || k == 3 || k == 9){
            for(int i = 0; i < n; i++){
                s.push_back('9');
            }
            return s;
        }
        if(k == 2){
            for(int i = 0; i < n; i++){
                s.push_back('9');
            }
            s[0] = '8';
            s[n - 1] = '8';
            return s;
        }
        if(k == 4){
            for(int i = 0; i < n; i++){
                s.push_back('9');
            }
            s[0] = '8';
            s[n - 1] = '8';
            if(n > 2){
                s[1] = '8';
                s[n - 2] = '8';
            }
            return s;
        }
        if(k == 8){
            for(int i = 0; i < n; i++){
                s.push_back('9');
            }
            s[0] = '8';
            s[n - 1] = '8';
            if(n > 2){
                s[1] = '8';
                s[n - 2] = '8';
            }
            if(n > 4){
                s[2] = '8';
                s[n - 3] = '8';
            }
            return s;
        }
        if(k == 5){
            for(int i = 0; i < n; i++){
                s.push_back('9');
            }
            s[0] = '5';
            s[n - 1] = '5';
            return s;
        }
        if(k == 6){
            long long int sum = 0;
            for(int i = 0; i < n; i++){
                s.push_back('9');
                sum += 9;
            }
            s[0] = '8';
            s[n - 1] = '8';
            sum -= 2;
            sum = sum % 3;
            if(n % 2 == 0){
                if(sum & 1) sum += 3;
                s[n/2] -= sum / 2;
                s[n - n/2 - 1] -= sum/2;
            }
            else{
                s[n/2] -= sum;
            }
            return s;
        }
        if(k == 7){
            for(int i = 0; i < n; i++){
                s.push_back('9');
            }
            for(int i = n/2; i >= 0; i--){
                for(int j = '9'; j >= '0'; j--){
                    s[i] = j;
                    s[n - i - 1] = j;
                    if(check(s)){
                        return s;
                    }
                }
            }
        }
        return s;
    }
};
